import React, { useState, useEffect } from "react";
import Posts from "../sections/ShowAllRecipes";
import Pagination from "../utills/Pagination";

import ShowAllrecipes from "../sections/ShowAllRecipes";
import { getData } from "../DAL/api";
const Gallery = ({ onAdd }) => {
  const [recipes, setRecipes] = useState([]);
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [recipesPerPage, setRecipesPerPage] = useState(5);


  useEffect(async () => {

    const fetchImages = await getData('images')
    const fetchRecipe = await await getData('recipes')
    console.log(fetchRecipe);
    setRecipes(fetchRecipe);
    setImages(fetchImages)
    if(!recipes.length>0) setLoading(true)
    // setTimeout(() => {
    //   setLoading(false);
    // }, 1500);
  }, []);

  // Get current posts
  const indexOfLastPost = currentPage * recipesPerPage;
  const indexOfFirstPost = indexOfLastPost - recipesPerPage;
  const currentPosts = recipes.slice(indexOfFirstPost, indexOfLastPost);

  // Change page
  const paginate = (pageNumber) => setCurrentPage(pageNumber);
  return (
    <div className="container=fluid mt-5 m-auto">
      <h3 className="text-primary mb-3">Recipes Gallery</h3>
      <select onChange={(e) => setRecipesPerPage(e.target.value)}>
        <option>quantity  </option>
        <option value={5}>5</option>
        <option value={10}>10</option>
        <option value={20}>20</option>
        <option value={50}>50</option>
        <option value={100}>100</option>
      </select>
      <ShowAllrecipes
        recipes={currentPosts}
        loading={loading}
        images={images}
        onAdd={onAdd}
      />
      <Pagination
        postsPerPage={recipesPerPage}
        totalPosts={recipes.length}
        paginate={paginate}
      />
    </div>
  );
};

export default Gallery;
