import { useEffect, useState } from "react";
import { getImages, getRecipes } from "../DAL/api";
import { Modal, ListGroupItem, Button, Image } from "react-bootstrap";
import { Carousel } from "react-bootstrap";
import { Link } from "react-router-dom";
import "./Carousel.css";
import "./ItemDetails.css";
function ItemDetails({ match, onAdd, product }) {
  const [item, setItem] = useState([]);
  const [images, setImages] = useState([]);
  console.log(match);
  const [openEditForm, setOpenEditform] = useState(false);
  const [list, setList] = useState([
    { username: "getaneh" },
    { username: "abebe" },
  ]);
  function edit() {
    setOpenEditform(!openEditForm);
  }

  useEffect(async () => {
    const arr = [];
    const item = await getRecipes();
    const fetchImages = await getImages();
    const displayItem = item.find((el) => el.recipeId == match.params.id);
    fetchImages.forEach((element) => {
      if (element.recipeId == match.params.id) arr.push(element.imgUrl);
      // setImages([...images, element.imgUrl])
    });
    setImages(arr);
    setItem(displayItem);
    // setImages(displayedImages);
  }, []);

  function getObj(array, filter) {
    return array.forEach((element) => {
      if (element.recipeId === filter) setImages([...images, element.id]);
    });
  }

  return (
    <div className="form-inner product">
      <Carousel fade>
        {images.length > 0 &&
          images.map((image, i) => (
            <Carousel.Item key={i}>
              <img className="m-auto" src={image} alt="1" />
              <Carousel.Caption>
                <h3 className="text-center">{item.name}</h3>
                <p>{item.description}</p>
              </Carousel.Caption>
            </Carousel.Item>
          ))}
      </Carousel>

      {console.log(images)}
      <div>
        <ListGroupItem> name: {item.name}</ListGroupItem>
        <ListGroupItem> prep time : {item.name} דקות</ListGroupItem>
        <ListGroupItem
          data-toggle="collapse"
          data-target="#collapseExample"
          aria-expanded="false"
          aria-controls="collapseExample"
        >
          {" "}
          ןצ {item.recipeId}
        </ListGroupItem>
        <ListGroupItem
          data-toggle="collapse"
          data-target="#collapseExample"
          aria-expanded="false"
          aria-controls="collapseExample"
        >
          {" "}
          preparation  {item.name}
        </ListGroupItem>

        <div class="collapse" id="collapseExample">
          <div class="card card-body">
            1:{item.name}
            2:{item.name}
            3:{item.name}
            4:{item.name}
          </div>
        </div>
      </div>

      <div>
        {/* <Button onClick={props.onHide}>חזור</Button> */}
        <Link className="btn btn-primary m-2" to={`/edit/${item.recipeId}`}>
          ערוך
        </Link>

        <Link className="btn btn-success" to={`/cart/${item.recipeId}`}>
          {" "}
          <div>
            <button onClick={() => onAdd(product)}>Add To Cart</button>
          </div>
        </Link>
      </div>
    </div>
  );
}

export default ItemDetails;
