import { useEffect, useState } from 'react'
import { getData } from '../DAL/api'
import { Multiselect } from 'multiselect-react-dropdown'
import { Row } from 'react-bootstrap'
import * as Yup from 'yup'
const Search = () => {
    const [recipes, setRecipes] = useState([])
    const [ingredients, setIngredients] = useState([])
    const [selectedIngredients, setSelectedIngredients] = useState([])
    const [diets, setDiets] = useState([])
    const [selectedDiets, setSelectedDiets] = useState([])
    const [categories, setCategories] = useState([])
    const [selectedcategories, setSelectedcategories] = useState([])
    const [recipeText, setRecipeText] = useState('')

    useEffect(async () => {
        const getAllRecipes = await getData('recipes')
        const getIngredients = await getData('ingredients')
        const getCategories = await getData('categories')
        const getDiets = await getData('diets')
        setRecipes(getAllRecipes)
        setIngredients(getIngredients)
        setCategories(getCategories)
        setDiets(getDiets)
    }, [])

    const onSelcted1 = (data) => {
        console.log(selectedIngredients);
        setSelectedIngredients(data)


        // console.log(selectedIngredients);
        // setSelectedIngredients([...selectedIngredients, data])

    }
    return <div>
        <h1>Multi Select</h1>
        <Row className='d-flex container-fluid m-auto'>
            <div>
                <label id='recipe'>
                    
                    recipe</label>
               
            </div>
            <div>
                {ingredients.length && <Multiselect options={ingredients}
                    displayValue={'name'} onSelect={onSelcted1}
                    placeholder='search ingredient' />}
            </div>
            <div>
                {categories.length && <Multiselect options={categories}
                    displayValue={'name'}
                    placeholder='category' />}
            </div>
            <div>
                {diets.length && <Multiselect options={diets}
                    displayValue={'name'} placeholder='diet type' />}
            </div>
        </Row>



    </div>


}
export default Search;
