import React, { useState, useEffect } from "react";
import demoData from "./demoData";
import CardItem from "../../CardItem";
import { Form, Col, Row } from "react-bootstrap";
import MyVerticallyCenteredModal from "../Modal";
function SearchRecipes() {
  const dataList = demoData();
  const [searchText, setSearchText] = useState("");
  const [data, setData] = useState(dataList);
  const [order, setOrder] = useState("color");
  const [currentRecipe, SetCurrenRecipe] = useState({});
  const [modalShow, setModalShow] = useState(false);
  const [selecteduser, setSelectedUser] = useState({});

  function changeUser(user) {
    console.log(user)
    setSelectedUser(user);
    setModalShow(true);
  }

 
  // handle change event of search input
  const handleChange = (value) => {
    setSearchText(value);
    filterData(searchText);
  };

  // filter records by search text
  const filterData = (searchText) => {
    const lowercasedValue = searchText.toLowerCase().trim();
    if (lowercasedValue === "") setData(dataList);
    else {
      const filteredData = dataList
        .sort((a, b) => (a[order] < b[order] ? -1 : 0))
        .filter((item) => {
          return item.name.includes(searchText);
          // return Object.keys(item).some((key) =>
          //   excludeColumns.includes(key)
          //     ? false
          //     : item[key].toString().toLowerCase().includes(lowercasedValue)
          // );
        });
      setData(filteredData);
    }
  };

  useEffect(() => {
    return filterData(searchText);
  }, [order, searchText]);
  return (
    <div className="App">
      <Row className="container">
        <Col>
          חיפוש:{" "}
          <input
            style={{ marginLeft: 5 }}
            type="text"
            placeholder="חפש מתכונים..."
            value={searchText}
            onChange={(e) => handleChange(e.target.value)}
          />
        </Col>
        <Col className="col-sm-2">
          <small>מיין לפי:</small>
          <Form.Check
            type="radio"
            label="תאריך"
            value={"year"}
            name="public"
            onChange={() => {
              setOrder("year");
            }}
          />
          <Form.Check
            type="radio"
            value={"name"}
            label="שם"
            name="public"
            onChange={(e) => {
              console.log("name");
              setOrder("name");
            }}
          />
        </Col>
      </Row>
      <MyVerticallyCenteredModal
                    show={modalShow}
                    user={selecteduser}
                    onHide={() => setModalShow(false)}
                  />
      <div className="box-container d-flex flex-wrap text-center">
        {searchText.length > 0 &&
          data.map((d, i) => {
           
            return (
              <span  onClick={() => changeUser(d)}> 
              <CardItem
                text={d.name}
                label={d.year}
                src={d.src}
                key={i}
                onClick={() => changeUser(d)}
              />
              </span>
              
            );
            
          })}
        <div className="clearboth"></div>
        {data.length === 0 && <span>לא נמצא</span>}
      </div>
    
    </div>
  );
}

export default SearchRecipes;
