import React from "react";
import { Link } from "react-router-dom";
import { Spinner } from "react-bootstrap";
import CardItem from "./CardItem";
const ShowAllRecipes = ({ recipes, loading }) => {
  if (loading) {
    return (
      <h2>
        {" "}
        טוען
        <Spinner animation="border" />
      </h2>
    );
  }

  return (
    <ul className="list-group mb-4 d-flex flex-row">
      {recipes.map((recipe) => (
        <Link to={`/products/${recipe.recipeId}`}>
          <CardItem src="https://images.pexels.com/photos/372882/pexels-photo-372882.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" />
          <li>{recipe.name}</li>
        </Link>
      ))}
    </ul>
  );
};

export default ShowAllRecipes;
