import React, { createContext, useReducer } from "react";
import AppReducer from "./AppReducer";
import { getRecipes, getUsers } from "../DAL/api";
import { ADD_TO_CART, REMOVE_FROM_CART, CLEAR_ALL_FROM_CART } from "./types";
import { useEffect } from "react";
import Context from "./Context";
import Reducer from "./Reducer";
const removeRecipe = (id) => {
  dispatch({
    type: "REMOVE_RECIPE",
    payload: id,
  });
};

const editRecipe = (id) => {
  dispatch({
    type: "EDIT_RECIPE",
    payload: id,
  });
};
const addRecipe = (recipe) => {
  dispatch({
    type: "EDIT_RECIPE",
    payload: recipe,
  });
};

const addUser = (user) => {
  dispatch({
    type: "ADD_USER",
    payload: user,
  });
};

const editUser = (user) => {
  dispatch({
    type: "EDIT_USER",
    payload: user,
  });
};
const removeUser = (id) => {
  dispatch({
    type: "EDIT_USER",
    payload: id,
  });
};


const addProductToCart = (product) => {
  dispatch({
    type: ADD_TO_CART,
    payload: product,
  });
};

// # remove product from cart
const removeProductFromCart = (productID) => {
  dispatch({
    type: REMOVE_FROM_CART,
    payload: productID,
  });
};

// Initial State
const initialState = {
  recipes: [],
  products: [],
  carts: [],
  addProductToCart: (product) => { },
  removeProductFromCart: (productID) => { },
  clearCart: () => { },
};
const products = [
  {
    id: 0,
    title: "Gaming Mouse",
    imageURL:
      "https://m.media-amazon.com/images/I/51S6IQ2lGwL._AC_UY327_FMwebp_QL65_.jpg",
    price: 49.5,
  },
  {
    id: 1,
    title: "Gaming Chair",
    imageURL:
      "https://m.media-amazon.com/images/I/61HEqHMkRhL._AC_UL480_FMwebp_QL65_.jpg",
    price: 149.0,
  },
  {
    id: 2,
    title: "Gaming Pad",
    imageURL:
      "https://m.media-amazon.com/images/I/71k--F6wVGL._AC_UL480_FMwebp_QL65_.jpg",
    price: 34.5,
  },
];

// Create Context
export const GlobalContext = createContext(initialState);

// Provider Component
export const GlobalProvider = ({ children }) => {
  const [state, dispatch] = useReducer(AppReducer, initialState);

  useEffect(async () => {
    const recipes = await getRecipes();
    const users = await getUsers();
    initialState.recipes = recipes;
    initialState.users = users;
    console.log("initialstate", initialState);
  }, []);

  // Actions

  // # clear all product from cart
  const clearCart = () => {
    dispatch({
      type: CLEAR_ALL_FROM_CART,
    });
  };

  return (
    <GlobalContext.Provider
      value={{
        users: state.users,
        removeUser,
        addUser,
        editUser,
        products,
        addProductToCart,
        removeProductFromCart,
        clearCart,
        carts: state.carts,
      }}
    >
      {children}
    </GlobalContext.Provider>
  );
};
