import React, { useState, useEffect } from "react";
import Posts from "./ShowAllRecipes";
import Pagination from "../utills/Pagination";
import { getRecipes } from "../DAL/api";
import ShowAllrecipes from "./ShowAllRecipes";

const App = () => {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [recipesPerPage, setRecipesPerPage] = useState(2);

  useEffect(() => {
    const fetchRecipes = async () => {
      setLoading(true);
      const res = await getRecipes();
      setRecipes(res);
      setLoading(false);
    };
    fetchRecipes();
  }, []);

  // Get current posts
  const indexOfLastPost = currentPage * recipesPerPage;
  const indexOfFirstPost = indexOfLastPost - recipesPerPage;
  const currentPosts = recipes.slice(indexOfFirstPost, indexOfLastPost);

  // Change page
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div className="container mt-5">
      <h1 className="text-primary mb-3">My Blog</h1>
      <select onChange={(e) => setRecipesPerPage(e.target.value)}>
        <option value={5}>5</option>
        <option value={10}>10</option>
        <option value={20}>20</option>
        <option value={50}>50</option>
        <option value={100}>100</option>
      </select>
      <ShowAllrecipes recipes={currentPosts} loading={loading} />
      <Pagination
        postsPerPage={recipesPerPage}
        totalPosts={recipes.length}
        paginate={paginate}
      />
    </div>
  );
};

export default App;
