import { useEffect, useState } from "react";
import { Row } from "react-bootstrap";
import validateInfo from "./validateInfo";
import { getUsers, login } from "../DAL/api";

function Login() {
  const [password, setPasseord] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const [users, setUsers] = useState([]);

  function changePassword(e) {
    console.log("password");
    setPasseord(e.target.value);
  }
  const login = (email, password) => {
    users.find((user) => user.email === email && user.password == password);
  };

  function changeEmail(e) {
    console.log("email");
    setEmail(e.target.value);
  }
  function onSubmit(e) {
    e.preventDefault();
    if (!!login(email, password)) {
      setMessage("");
      alert("welcome");
    } else setMessage("טעות בפרטים, אנא נסה שוב");
  }

  useEffect(async () => {
    const users = await getUsers();
    setUsers(users);
  });
  return (
    <form>
      <div className="container form-inner">
        <h1>כניסה</h1>

        <label htmlFor="email" className="d-block">
          אימייל
        </label>
        {message && <small>{message}</small>}
        <input
          id="email"
          name="email"
          type="text"
          className="form-control"
          onChange={(e) => {
            changeEmail(e);
          }}
          defaultValue={email}
        />
        <label htmlFor="email">סיסמא</label>
        <input
          id="password"
          name="password"
          type="text"
          className="form-control"
          onChange={(e) => {
            changePassword(e);
          }}
          aria-describedby="emailHelp"
          defaultValue={password}
        />
        <Row>
          <button
            className="btn btn-primary my-2 text-right font-weight-bold "
            type="submit"
            onClick={onSubmit}
          >
            כניסה
          </button>
          <button className="btn btn-primary my-2 mx-3">ביטול</button>
        </Row>
        <Row>
          <small>
            <a href="/sign-up">אין משתמש?לחץ להרשמה </a>
          </small>
        </Row>
      </div>
    </form>
  );
}
export default Login;
