import React from "react";
import "./registerform.css";
// import { useHistory } from "react-router-dom";
import {
  Form,
  Col,
  InputGroup,
  ToggleButton,
  Button,
  ButtonGroup,
  Row,
} from "react-bootstrap";
import InputCreator from "./InputCreator";
import validate from "./validateInfo";
import useForm from "./useForm";
// import "./AddUser.css";
import ErrorMessages from "./ErrorMessages";

const RegisterForm = (props) => {
  //   const history = useHistory();
  const { handleSubmit, handleChange, values, errors } = useForm(
    submit,
    validate
  );

  function submit(e) {
    props.addUser(values);
    // history.push("/users");
  }

  return (
    <Form
      className="text-left container w-50 m-auto rounded"
      onSubmit={handleSubmit}
    >
      <Form.Row className="m-auto">
        <h1 className="text-center">Student details</h1>
      </Form.Row>
      <Form.Row>
        <p>hello student,please fill your details</p>
      </Form.Row>
      <hr />

      <Form.Group  controlId="formGridEmail">
        <InputCreator
          title="שם משתמש"
          type="text"
          required="true"
          value={values.username}
          placeholder="Enter Username"
          sign={<i class="fas fa-user"></i>}
          onChange={handleChange}
          name="username"
          error={errors.username}
        />
        {errors.username && <ErrorMessages errors={errors.username} />}
      </Form.Group>

      <Form.Group  controlId="formGridPassword">
        <InputCreator
          title="אימייל"
          type="email"
          required="true"
          value={values.email}
          placeholder="Enter email"
          sign={<i class="fas fa-mail-bulk"></i>}
          onChange={handleChange}
          name="email"
          error={errors.email}
        />
        {errors.username && (
          <ul>
            {errors.email.map((error, i) => (
              <li key={i}>{error}</li>
            ))}
          </ul>
        )}
      </Form.Group>

      <InputCreator
        title="סיסמא"
        type="password"
        required="true"
        value={values.email}
        placeholder="Enter email"
        sign={<i class="fas fa-mail-bulk"></i>}
        onChange={handleChange}
        name="email"
        error={errors.email}
      />

      <InputCreator
        title="אימות סיסמא"
        type="password"
        required="true"
        value={values.email}
        placeholder="Enter email"
        sign={<i class="fas fa-mail-bulk"></i>}
        onChange={handleChange}
        name="email"
        error={errors.email}
      />

      <Button
        className="btn btn-primary my-2 w-100 font-weight-bold "
        type="submit"
        onClick={handleSubmit}
      >
        Submit
      </Button>
    </Form>
  );
};

export default RegisterForm;
