import { useEffect, useState } from "react";
import { getRecipes } from "../DAL/api";

function ItemDetails({ match }) {
  console.log(match);
  const [openEditForm, setOpenEditform] = useState(false);
  const [list, setList] = useState([
    { username: "getaneh" },
    { username: "abebe" },
  ]);
  function edit() {
    setOpenEditform(!openEditForm);
  }

  const [item, setItem] = useState([]);
  useEffect(async () => {
    const item = await getRecipes();
    console.log("aa", item);
    const displayItem = item.find((el) => el.recipeId == match.params.id);
    console.log("display", displayItem);
    setItem(displayItem);
  });

  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          {}
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Image
          id="modal-img"
          style={{ margin: "1rem 5rem" }}
          variant="ml-5 text-center"
          src={props.user.src}
          rounded
        />
        <ListGroupItem> שם: {item.name}</ListGroupItem>
        <ListGroupItem> זמן הכנה: {item.name} דקות</ListGroupItem>
        <ListGroupItem
          data-toggle="collapse"
          data-target="#collapseExample"
          aria-expanded="false"
          aria-controls="collapseExample"
        >
          {" "}
          מצרכים {props.user.grade}
        </ListGroupItem>
        <ListGroupItem
          data-toggle="collapse"
          data-target="#collapseExample"
          aria-expanded="false"
          aria-controls="collapseExample"
        >
          {" "}
          אופן ההכנה {item.name}
        </ListGroupItem>

        <div class="collapse" id="collapseExample">
          <div class="card card-body">
            1:{item.name}
            2:{item.name}
            3:{props.user.name}
            4:{props.user.name}
          </div>
        </div>
      </Modal.Body>

      <Modal.Footer>
        <Button onClick={props.onHide}>חזור</Button>
        <Button onClick={() => edit()}>ערוך</Button>
        <Button className="btn btn-success" onClick={<Cart cartList={list} />}>
          {" "}
          הוסף מצרכים לעגלה
        </Button>
      </Modal.Footer>
      {openEditForm && <EditForm user={props.user} />}
    </Modal>
  );
}

export default ItemDetails;
