import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Button, Spinner } from "react-bootstrap";
import Pagination from "../utills/Pagination";
import CardItem from "./CardItem";
import { Form, Row, Col } from "react-bootstrap";

import { getData, orderBy } from "../DAL/api";
const ShowAllRecipes = (props) => {
  const { recipes, images, loading, onAdd } = props
  const [newImages, setNewImages] = useState([]);
  const [orderListBy, setOrderListBy] = useState('asc')
  const [newRecipes, setNewRecipes] = useState([])
  const [listState, setListState] = useState('name')

  useEffect(() => {
    return order()
  }, [orderListBy, listState])
  if (loading) {
    return (
      <h2>
        Loading
        <Spinner animation="border" />
      </h2>
    );
  }


  const order = async () => {
    const value = listState
    const response = await orderBy(value, orderListBy)
    setNewRecipes(response)
  }

  const changeState = () => {
    if (orderListBy === 'asc') {
      setOrderListBy('desc')

    }
    if (orderListBy === 'desc') {
      setOrderListBy('asc')

    }
  }
  const changeListState = (e) => {
    console.log(e.target.value);
    setListState(e.target.value)


  }

  function getObj(array, filter) {
    const image = array.find((item) => item.recipeId == filter)
    if (image) return image.imgUrl
  }


  // Change page

  return (
    <>
      <div className='d-flex justify-content-center flex-row'>
        <div class="form-check">
          <input onClick={(e) => changeListState(e)} class="form-check-input" type="radio" name="exampleRadios" id="exampleRadios1" value="name" />
          <label class="form-check-label" for="exampleRadios1">
            name
          </label>
        </div>
        <div class="form-check">
          <input onClick={(e) => changeListState(e)} class="form-check-input" type="radio" name="exampleRadios" id="exampleRadios1" value="views" />
          <label class="form-check-label" for="exampleRadios1">
            views
          </label>
        </div>
        <div class="form-check">
          <input onClick={(e) => changeListState(e)} class="form-check-input" type="radio" name="exampleRadios" id="exampleRadios2" value="date" />
          <label class="form-check-label" for="exampleRadios2">
            Date
          </label>

        </div>
        <h1 vlaue='date' onClick={changeState} className='ml-3'><i class="fas fa-sort-amount-up-alt"></i></h1>
      </div>
      console.log(newRecipes);

      <div className='d-flex flex-wrap'>
        {recipes.map((recipe, i) => (
          

          <li className='' key={i} >
            <CardItem
              path={`/products/${recipe.id}`}
              src={recipe.images[0] ? `http://localhost:3200/static/${recipe.images[0].url} ` : null

              }
              text={recipe.name}
              views={recipe.views}
              label={recipe.name}
              onAdd={onAdd}

            />
          </li>
        ))}

      </div>

    </>
  );
};

export default ShowAllRecipes;
