import { useEffect, useState } from "react";
import {
  Accordion,Card,Button,Form,Col,Row,Table} from "react-bootstrap";
import useForm from "../utills/useForm";
import validate from "../utills/validateInfo";
import { getIngredients, getUnits, getDiets, getCategories } from "../DAL/api";

function NewRecipeForm(props) {
  const [ingredients, setIngredients] = useState([]);
  const [units, setUnits] = useState([]);
  const [instructions, setInstructions] = useState([]);
  const [guide, setGuide] = useState([]);
  const [diets, setDiets] = useState([]);
  const [categories, setCategories] = useState([]);
  const { handleSubmit, handleChange, values, errors } = useForm(submit,validate);
  const deleteInstruction = (index) => {
    instructions.splice(index, 1);
    setInstructions([...instructions]);
  };
  const deleteGuide = (index) => {
    guide.splice(index, 1);
    setGuide([...guide]);
  };


  function submit(e) {
    props.addUser(values);
  }

  useEffect(async () => {
    const fetchIngredients = await getIngredients();
    const fetchUnits = await getUnits();
    const fetchDiets = await getDiets();
    const fetchCategories = await getCategories();
    setUnits(fetchUnits);
    setIngredients(fetchIngredients);
    setDiets(fetchDiets);
    setCategories(fetchCategories);
  }, []);
  return (
    <Form onSubmit={handleSubmit}>
      <div className="form-inner">
        <h1>הוספת מתכון</h1>
        <Row className="container">
          <Col className="col-md-3">
            <Form.Label>שם מתכון</Form.Label>
            <Form.Control type="text" name="name" onChange={handleChange} />
          </Col>
          <Col className="col-md-3 mr-4">
            <Row>
              <Form.Label as="Row" column sm={10}>
                מי יכול לצפות
              </Form.Label>
            </Row>
            <Row sm={10}>
              <Form.Check
                type="radio"
                label="כולם"
                value={false}
                name="isPrivate"
                onChange={handleChange}
              />
              <Form.Check
                type="radio"
                value={true}
                label="רק אני"
                name="isPrivate"
                onChange={handleChange}
              />
            </Row>
          </Col>
          <Col>
            <Form.File
              id="exampleFormControlFile1"
              label="תמונה ראשית"
              name="image"
              onChange={handleChange}
            />
          </Col>
        </Row>
        <Row className="container-fluid">
          <Form.Label>מקור המתכון </Form.Label>
          <Form.Control
            type="text"
            placeholder="מקור המתכון"
            name="source"
            onChange={handleChange}
          />
          <Form.Label>קישור למקור </Form.Label>
          <Form.Control
            type="text"
            placeholder=" אם קיים קישור למתכון"
            name="sourceUrl"
            onChange={handleChange}
          />
        </Row>

        <Accordion defaultActiveKey="0">
          <Card>
            <Card.Header>
              <Accordion.Toggle as={Button} variant="link" eventKey="3">
                תיאור כללי
              </Accordion.Toggle>
            </Card.Header>
            <Accordion.Collapse eventKey="3">
              <div>
                <Form.Group
                  className="mb-3 container-fluid"
                  controlId="exampleForm.ControlTextarea1"
                >
                  <Form.Label>
                    תיאור כללי של המתכון,יופיע מתחת לכותרת
                  </Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={2}
                    name="description"
                    onChange={handleChange}
                  />
                </Form.Group>
                <Row className="container w-75 m-auto">
                  <Col>
                    <Row className="mr-3">מתאים לתזונה מסוג:</Row>
                    <Row>
                      {diets.length &&
                        diets.map((diet) => {
                          return (
                            <Form.Check
                              type="checkbox"
                              label={diet.name}
                              name={diet.name}
                              value={diet.id}
                              onChange={handleChange}
                            />
                          );
                        })}
                    </Row>
                  </Col>

                  <Col>
                    <Row className="mr-3">סוג מנה</Row>
                    <Row>
                      {categories.length &&
                        categories.map((category) => {
                          return (
                            <Form.Check
                              type="checkbox"
                              label={category.name}
                              name={category.name}
                              value={category.id}
                              onChange={handleChange}
                            />
                          );
                        })}
                    </Row>
                  </Col>
                </Row>
              </div>
            </Accordion.Collapse>
          </Card>
          <Card>
            <Card.Header>
              <Accordion.Toggle as={Button} variant="link" eventKey="0">
                פירוט הרכיבים
              </Accordion.Toggle>
            </Card.Header>
            <Accordion.Collapse eventKey="0">
              <Row>
                <Row>
                  <Table
                    triped
                    bordered
                    hover
                    size="sm"
                    className="m-auto text-center "
                  >
                    <tbody>
                      {instructions.length > 0 &&
                        instructions.map((item, i) => (
                          <tr key={i}>
                            <td>
                              {(item, item)}
                              <Button
                                className="btn-danger px-1 my-1 "
                                onClick={() => deleteInstruction(i)}
                              >
                                <i class="fas fa-trash text-sm"></i>
                              </Button>
                            </td>
                            <td>{item}</td>
                            <td>{item}</td>
                          </tr>
                        ))}
                    </tbody>
                  </Table>
                </Row>
                <Row>
                  <Col className="col-md-1">
                    <Form.Label>כמות</Form.Label>
                    <Form.Control type="number" />
                  </Col>
                  <Col className="col-md-2">
                    <Form.Label>יחידות מידה</Form.Label>
                    <select onChange={handleChange}>
                      {units.map((unit, i) => {
                        return (
                          <option key={i} value={unit.id}>
                            {unit.name}
                          </option>
                        );
                      })}
                    </select>
                  </Col>

                  <Col>
                    <Form.Label>רכיבים</Form.Label>

                    <select onChange={handleChange}>
                      {ingredients.map((ingredient, i) => {
                        return (
                          <option key={i} value={ingredient.id}>
                            {ingredient.name}
                          </option>
                        );
                      })}
                    </select>
                  </Col>
                  <Col className="col-md-2">
                    <Form.Label>הערות</Form.Label>

                    <Form.Control as="input" />
                  </Col>
                  <Col className="flex-grow-0">
                    <Button
                      className="m-2 flex-grow-0"
                      onClick={() => setInstructions([...instructions, "get"])}
                    >
                      שמור
                    </Button>
                    <Button>נקה</Button>
                  </Col>
                </Row>
              </Row>
            </Accordion.Collapse>
          </Card>
          <Card>
            <Card.Header>
              <Accordion.Toggle as={Button} variant="link" eventKey="1">
                פירוט שלבי ההכנה
              </Accordion.Toggle>
            </Card.Header>
            <Accordion.Collapse eventKey="1">
              <Row className="container">
                <Row>
                  <Table
                    triped
                    bordered
                    hover
                    size="sm"
                    className="m-auto text-center "
                  >
                    <tbody>
                      {guide.length > 0 &&
                        guide.map((item, i) => (
                          <tr key={i}>
                            <td>
                              {(item, item)}
                              <Button
                                className="btn-danger px-1 my-1 "
                                onClick={() => deleteGuide(i)}
                              >
                                <i class="fas fa-trash text-sm"></i>
                              </Button>
                            </td>
                            <td>{item}</td>
                            <td>{item}</td>
                          </tr>
                        ))}
                    </tbody>
                  </Table>
                </Row>
                <Row>
                  <Col className="col-md-2">
                   
                    <Form.Control as="input" />
                  </Col>
                  <Col className="col-md-4">
                    <Form.Label>הוראה</Form.Label>
                    <Form.Control as="input" />
                  </Col>
                  <Col className="col-md-2">
                    <Button
                      className="m-2 flex-grow-0"
                      onClick={() => setGuide([...guide, "get"])}
                    >
                      שמור
                    </Button>
                  </Col>
                </Row>
              </Row>
            </Accordion.Collapse>
          </Card>
        </Accordion>
        <Row className="text-left">
          <Button type="submit" className="m-3">
            שמור מתכון
          </Button>
        </Row>
      </div>
    </Form>
  );
}
export default NewRecipeForm;
