import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Spinner } from "react-bootstrap";
import Pagination from "../utills/Pagination";
import CardItem from "./CardItem";
import { Form, Row, Col } from "react-bootstrap";
import { getData, orderBy } from "../DAL/api";
const ShowAllRecipes = (props) => {
  const { recipes, images, loading, onAdd } = props
  // const [order, setOrder] = useState("color");
  const [newImages, setNewImages] = useState([]);
  const [orderListBy, setOrderListBy] = useState('asc')
  const [newRecipes, setNewRecipes] = useState([...recipes])
  const [listState, setListState] = useState('name')
  const [orderState, setOrderState] = useState('')
  const [currentPage, setCurrentPage] = useState(1);
  const [recipesPerPage, setRecipesPerPage] = useState(5);

  if (loading) {
    return (
      <h2>
        Loading
        <Spinner animation="border" />
      </h2>
    );
  }
  const order = async () => {
    const value = listState
    const response = await orderBy(value, orderListBy)
    console.log(response);
    setNewRecipes(response)
  }





  const changeState = () => {
    if (orderListBy === 'asc') {
      setOrderListBy('desc')
      order()
    }
    if (orderListBy === 'desc') {
      setOrderListBy('asc')
      order()
    }
  }
  const changeListState = (e) => {
    setListState(e.target.value)
    oreder()

  }
  function getObj(array, filter) {
    const image = array.find((item) => item.recipeId == filter)
    if (image) return image.imgUrl
  }


  // Change page

  return (
    <>
      <button value='name' onClick={(e) => changeListState} className='m-5'>orderBY Name</button>
      <button value='views' onClick={() => { setListState('views') }} className='m-5'>orderBY views</button>
      <button vlaue='date' onClick={() => { setListState('date') }}>orderBY date</button>
      <button vlaue='date' onClick={changeState}>{orderListBy}</button>
      <ul className=" d-flex flex-wrap justify-content-center  ">
        {newRecipes.map((recipe) => (
          <CardItem
            path={`/products/${recipe.recipeId}`}
            src={
              getObj(images, recipe.recipeId) ||
              "https://images.pexels.com/photos/4735285/pexels-photo-4735285.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"
            }
            text={recipe.name}
            views={recipe.views}
            label={recipe.name}
            onAdd={onAdd}
          />
        ))}
      </ul>

    </>
  );
};

export default ShowAllRecipes;
