import React from "react";
import "./registerform.css";
// import { useHistory } from "react-router-dom";
import {
  Form,
  Col,
  InputGroup,
  ToggleButton,
  Button,
  ButtonGroup,
  Row,
} from "react-bootstrap";
import InputCreator from "./InputCreator";
import validate from "./validateInfo";
import useForm from "./useForm";
// import "./AddUser.css";
import ErrorMessages from "./ErrorMessages";

const RegisterForm = (props) => {
  //   const history = useHistory();
  const { handleSubmit, handleChange, values, errors } = useForm(
    submit,
    validate
  );

  function submit(e) {
    props.addUser(values);
    // history.push("/users");
  }

  return (
    <>
      <Form dir="rtl" onSubmit={handleSubmit}>
        <div className="form-inner">
          <Form.Row className="m-auto rtl">
            <h1 className="text-center">רישום למחברת מתכונים</h1>
          </Form.Row>
          <hr />
          <Form.Group>
            <InputCreator
              title="שם משתמש"
              type="text"
              required="true"
              value={values.username}
              placeholder="שם משתמש"
              sign={<i class="fas fa-user"></i>}
              onChange={handleChange}
              name="username"
              error={errors.username}
            />
            {errors.username && <ErrorMessages errors={errors.username} />}
          </Form.Group>
          <Form.Group controlId="formGridPassword">
            <InputCreator
              title="אימייל"
              type="email"
              required="true"
              value={values.email}
              placeholder="כתובת מייל הנמצאת בשימוש"
              sign={<i class="fas fa-mail-bulk"></i>}
              onChange={handleChange}
              name="email"
              error={errors.email}
            />
            {errors.email && <ErrorMessages errors={errors.email} />}
          </Form.Group>
          <Form.Group>
            <InputCreator
              title="סיסמא"
              type="password"
              required="true"
              value={values.password}
              placeholder="סיסמא"
              sign={<i class="fas fa-mail-bulk"></i>}
              onChange={handleChange}
              name="password"
              error={errors.password}
              note=" דרישות מינימום לסיסמא תקינה: לפחות ספרה, אות קטנה ואות גדולה
            (באנגלית). ולא פחות מ-8 תווים"
            />
            {errors.password && <ErrorMessages errors={errors.password} />}
          </Form.Group>
          <Form.Group>
            <InputCreator
              title="אימות סיסמא"
              type="password"
              required="true"
              value={values.password2}
              placeholder=""
              sign={<i class="fas fa-mail-bulk"></i>}
              onChange={handleChange}
              name="password2"
              error={errors.password2}
              note="תוכן השדה יהיה זהה לתוכן שהוקלד עבור הסיסמא"
            />
            {errors.password2 && <ErrorMessages errors={errors.password2} />}
          </Form.Group>
          <Form.Group>
            <Row>
              <Col>דברים שאני רוצה</Col>
              <Col className="w-75  col-md-10">
                <Row className="d-flex ">
                  <Form.Check
                    type="checkbox"
                    label="טבעוני"
                    name="vegan"
                    value="vegan"
                    onChange={handleChange}
                  />
                  <Form.Check
                    type="checkbox"
                    label="פרווה"
                    name="parve"
                    value="parve"
                    onChange={handleChange}
                  />
                  <Form.Check
                    type="checkbox"
                    label="כשר"
                    name="kosher"
                    value="kosher"
                    onChange={handleChange}
                  />
                  <Form.Check
                    type="checkbox"
                    label="צמחוני"
                    name="vegetarian"
                    value="vegetarian"
                    onChange={handleChange}
                  />
                </Row>
                <Row className="d-flex">
                  <Form.Check
                    type="checkbox"
                    name="gluten-free"
                    label="ללא גלוטן"
                    value="glten-free"
                    onChange={handleChange}
                  />
                  <Form.Check
                    type="checkbox"
                    name="paleo"
                    label="פליאו"
                    value="paleo"
                    onChange={handleChange}
                  />
                  <Form.Check
                    type="checkbox"
                    name="stam"
                    label="קטנוני"
                    value="stam"
                    onChange={handleChange}
                  />
                  <Form.Check
                    type="checkbox"
                    name="raw"
                    label="רואו"
                    value="raw"
                    onChange={handleChange}
                  />
                </Row>
              </Col>
            </Row>
          </Form.Group>
          <div className="text-center">
            <Button type="submit" id="submit" onClick={handleSubmit}>
              רישום
            </Button>

            <small className="d-block mr-3">
              <a href="/login">משתמש קיים? לחץ כאן לכניסה</a>
            </small>
          </div>
        </div>
      </Form>
    </>
  );
};

export default RegisterForm;
