import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Spinner } from "react-bootstrap";
import Pagination from "../utills/Pagination";
import CardItem from "./CardItem";
import { Form, Row, Col } from "react-bootstrap";
import { getData, orderBy } from "../DAL/api";
const ShowAllRecipes = (props) => {
  const { recipes, images, loading, onAdd } = props
  // const [order, setOrder] = useState("color");
  const [newImages, setNewImages] = useState([]);
  const [newRecipes, setNewRecipes] = useState([...recipes])
  const [currentPage, setCurrentPage] = useState(1);
  const [recipesPerPage, setRecipesPerPage] = useState(5);
  console.log(recipes);

  if (loading) {
    return (
      <h2>
        Loading
        <Spinner animation="border" />
      </h2>
    );
  }
  const order = async (value) => {
    const response = await orderBy(value)
    console.log(response);
    setNewRecipes(response)
  }
  useEffect(()=>{

  },[recipesPerPage])

  function getObj(array, filter) {
    const image = array.find((item) => item.recipeId == filter)
    if (image) return image.imgUrl
  }
  const indexOfLastPost = currentPage * recipesPerPage;
  const indexOfFirstPost = indexOfLastPost - recipesPerPage;
  const currentPosts = newRecipes.slice(indexOfFirstPost, indexOfLastPost);

  // Change page
  const paginate = (pageNumber) => setCurrentPage(pageNumber);
  console.log(recipes);
  return (
    <>
      <button value='name' onClick={() => order('name')} className='m-5'>orderBY Name</button>
      <button value='views' onClick={() => { order('views') }} className='m-5'>orderBY views</button>
      <button vlaue='date' onClick={() => { order('date') }}>orderBY date</button>
      <select onChange={(e) => setRecipesPerPage(e.target.value)}>
        <option>quantity  </option>
        <option value={2}>5</option>
        <option value={10}>10</option>
        <option value={20}>20</option>
        <option value={50}>50</option>
        <option value={100}>100</option>
      </select>
      <ul className=" d-flex flex-wrap justify-content-center  ">
        {newRecipes.map((recipe) => (
          <CardItem
            path={`/products/${recipe.recipeId}`}
            src={
              getObj(images, recipe.recipeId) ||
              "https://images.pexels.com/photos/4735285/pexels-photo-4735285.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"
            }
            text={recipe.name}
            views={recipe.views}
            label={recipe.name}
            onAdd={onAdd}
          />
        ))}
      </ul>
      <Pagination
        postsPerPage={recipesPerPage}
        totalPosts={recipes.length}
        paginate={paginate}
      />
    </>
  );
};

export default ShowAllRecipes;
