import { useEffect, useState } from "react";
import { getImages, getRecipes } from "../DAL/api";
import { Modal, ListGroupItem, Button, Image } from "react-bootstrap";

function ItemDetails({ match }) {
  const [item, setItem] = useState([]);
  const [images, setImages] = useState([]);
  console.log(match);
  const [openEditForm, setOpenEditform] = useState(false);
  const [list, setList] = useState([
    { username: "getaneh" },
    { username: "abebe" },
  ]);
  function edit() {
    setOpenEditform(!openEditForm);
  }

  useEffect(async () => {
    const arr=[]
    const item = await getRecipes();
    const fetchImages = await getImages();
    const displayItem = item.find((el) => el.recipeId == match.params.id);
    fetchImages.forEach((element) => {
     
      if (element.recipeId == match.params.id) arr.push(element.imgUrl)
              // setImages([...images, element.imgUrl])
    }
   );
    setImages(arr)
    setItem(displayItem);
    // setImages(displayedImages);
  }, []);

  function getObj(array, filter) {
    return array.forEach((element) => {
      if (element.recipeId === filter) setImages([...images, element.id]);
    });
  }

  return (
    <div
      {...item}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      {/* <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">{}</Modal.Title>
      </Modal.Header> */}
      <div>
        <ul className='container d-flex justify-content-center'>
        <Carousel>
  <Carousel.Item>
    <img
      className="d-block w-100"
      src="holder.js/800x400?text=First slide&bg=373940"
      alt="First slide"
    />
    <Carousel.Caption>
      <h3>First slide label</h3>
      <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
    </Carousel.Caption>
  </Carousel.Item>
  <Carousel.Item>
    <img
      className="d-block w-100"
      src="holder.js/800x400?text=Second slide&bg=282c34"
      alt="Second slide"
    />

    <Carousel.Caption>
      <h3>Second slide label</h3>
      <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
    </Carousel.Caption>
  </Carousel.Item>
  <Carousel.Item>
    <img
      className="d-block w-100"
      src="holder.js/800x400?text=Third slide&bg=20232a"
      alt="Third slide"
    />

    <Carousel.Caption>
      <h3>Third slide label</h3>
      <p>Praesent commodo cursus magna, vel scelerisque nisl consectetur.</p>
    </Carousel.Caption>
  </Carousel.Item>
</Carousel>
          {images.length &&
            images.map((item, i) => (
              <li key={i}>
                {i}
                <Image
                  
                  style={{ width: "40%" ,margin:'1rem' }}
                  variant="text-center"
                  src={item}
                  rounded
                />
                {i}
              </li>
            ))}
        </ul>
        {console.log(images)}
        <ListGroupItem> שם: {item.name}</ListGroupItem>
        <ListGroupItem> זמן הכנה: {item.name} דקות</ListGroupItem>
        <ListGroupItem
          data-toggle="collapse"
          data-target="#collapseExample"
          aria-expanded="false"
          aria-controls="collapseExample"
        >
          {" "}
          מצרכים {item.recipeId}
        </ListGroupItem>
        <ListGroupItem
          data-toggle="collapse"
          data-target="#collapseExample"
          aria-expanded="false"
          aria-controls="collapseExample"
        >
          {" "}
          אופן ההכנה {item.name}
        </ListGroupItem>

        <div class="collapse" id="collapseExample">
          <div class="card card-body">
            1:{item.name}
            2:{item.name}
            3:{item.name}
            4:{item.name}
          </div>
        </div>
      </div>

      <div>
        {/* <Button onClick={props.onHide}>חזור</Button> */}
        <Button onClick={() => edit()}>ערוך</Button>
        <Button className="btn btn-success"> הוסף מצרכים לעגלה</Button>
      </div>
    </div>
  );
}

export default ItemDetails;
