import { useEffect, useState } from "react";
import fetch from "../DAL/api";
import { Modal, ListGroupItem, Button, Image } from "react-bootstrap";
import { Carousel } from "react-bootstrap";
import { Link } from "react-router-dom";
import "./Carousel.css";
import "./ItemDetails.css";
import { getRecipeIngredients, getData, getRecipe, getImage } from "../DAL/api";
const fetchData = require('../DAL/api')

function ItemDetails(props) {
  const { match, product, onAdd } = props
  const [item, setItem] = useState([]);
  const [images, setImages] = useState([]);
  const [instructions, setInstructions] = useState([]);
  const [ingredients, setIngredients] = useState([]);
  const [indredientsName, setIngredientsName] = useState([])
  const addToCart = () => {
    const arrayOfIngredients = [];
    const arrayFromLocal = JSON.parse(localStorage.getItem('cart')) || [];
    arrayFromLocal.push(ingredients);
    localStorage.setItem('cart', JSON.stringify(arrayOfIngredients));
  }




  useEffect(async () => {
    const recipeItem = await getData('recipes', match.params.id)
    setItem(recipeItem[0]);
    const fetchImages = await getData('images', match.params.id)
    setImages(fetchImages)
    const getIngredientsName = await getData('ingredients', match.params.id)
    setIngredients(getIngredientsName)
    const getInstructions = await getData('instructions', match.params.id)
    setInstructions(getInstructions)
  }, []);

  return (
    <div className="form-inner product">
      <Carousel fade>
        {images.length > 0 &&
          images.map((image, i) => (
            <Carousel.Item key={i}>
              <img src={image.imgUrl} alt="1" height='20px' />
              <Carousel.Caption>
                <h3 className="text-center">{item.name}</h3>
                <p>{item.description}</p>
              </Carousel.Caption>
            </Carousel.Item>
          ))}
      </Carousel>
      <div>
        <ListGroupItem> name: {item.name}</ListGroupItem>
        <ListGroupItem> prep time: {item.prep_time_min} minutes</ListGroupItem>
        <ListGroupItem
          data-toggle="collapse"
          data-target="#collapseExample"
          aria-expanded="false"
          aria-controls="collapseExample"
        >
          ingredients-click to see
        </ListGroupItem>
        <ListGroupItem
          data-toggle="collapse"
          data-target="#collapseExample1"
          aria-expanded="false"
          aria-controls="collapseExample1"
        >
          instrictions-click to see
        </ListGroupItem>

        <div class="collapse" id="collapseExample">
          <div class="card card-body">
            {ingredients.length > 0 && ingredients.map((inst, i) => {

              return <li key={i}>{inst.name} </li>
            })}
          </div>

        </div>
        <div class="collapse" id="collapseExample1">

          {instructions.length > 0 && instructions.map((inst, i) => {

            return <li key={i}>{inst.instruction} </li>
          })}
        </div>
      </div>
      <div>
        <Link className="btn btn-primary m-2" to={`/edit/${item.recipeId}`}>
          Edit
        </Link>
        <Link className="btn btn-success">
          <div>
            <button onClick={() => addToCart()} >Add To Cart</button>
          </div>
        </Link>
      </div>
    </div>
  );
}

export default ItemDetails;
