import React, { useState, useEffect } from "react";
import demoData from "./demoData";
import CardItem from "./CardItem";
import { Form, Col, Row } from "react-bootstrap";
import MyVerticallyCenteredModal from "../utills/Modal";
import { getRecipes, getImages } from "../DAL/api";
import Pagination from "../utills/Pagination";
import { Link } from "react-router-dom/cjs/react-router-dom.min";
import Cards from "./Cards";
function SearchRecipes() {
  const [searchText, setSearchText] = useState("");
  const [images, setImages] = useState([]);
  const [data, setData] = useState([]);
  const [order, setOrder] = useState("color");
  const [modalShow, setModalShow] = useState(false);
  const [selecteduser, setSelectedRecipe] = useState({});
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage] = useState(5);

  function changeRecipe(recipe) {
    setSelectedRecipe(recipe);
    setModalShow(true);
  }
  function getObj(array, filter) {
    return array.find((item) => item.recipeId === filter);
  }

  // handle change event of search input
  const handleChange = (value) => {
    setSearchText(value);
  };

  const pagination = async (pageNum, size, sortObj, order = "asc") => {
    const recipes = await getRecipes();
   
    const newRecipes = recipes.sort((a, b) =>
      order == "asc"
        ? a.sortObj - b.sortObj > 1
          ? -1
          : 0
        : b.sortObj - a.sortObj > 1
        ? -1
        : 0
    );
    return newRecipes.slice((pageNum - 1) * size, pageNum * size);
  };

  // const indexOfLastPost = currentPage * postsPerPage;
  // const indexOfFirstPost = indexOfLastPost - postsPerPage;
  // const currentPosts = [...data];
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  useEffect(async () => {
    const fetchedImages = await getImages();
    const fetchedRecipes = await getRecipes();
    setData(fetchedRecipes);
    setImages(fetchedImages);

    
  }, []);

  useEffect(() => {}, [order, searchText]);

  return (
    <div>
      <Row className="container-fluid mx-auto">
        <Row>
          <Col>
            חיפוש:{" "}
            <input
              style={{ marginLeft: 5 }}
              type="text"
              placeholder="חפש מתכונים..."
              value={searchText}
              onChange={(e) => handleChange(e.target.value)}
            />
          </Col>
          <Col className="col-sm-2 d-flex">
            <h6 className="d-block mx-3">מיין לפי:</h6>
            <Form.Check
              type="radio"
              label="תאריך"
              value={"year"}
              name="public"
              onChange={() => {
                setOrder("year");
              }}
            />
            <Form.Check
              type="radio"
              value={"name"}
              label="שם"
              name="public"
              onChange={(e) => {
                setOrder("name");
              }}
            />
            <Form.Check
              type="radio"
              value={"views"}
              label="צפיות"
              name="public"
              onChange={(e) => {
                setOrder("views");
              }}
            />
          </Col>
        </Row>
      </Row>
      <MyVerticallyCenteredModal
        show={modalShow}
        user={selecteduser}
        onHide={() => setModalShow(false)}
      />
      <div className="box-container  flex-wrap text-center">
        <ul>
          {searchText &&
            data
              .sort((a, b) => (a[order] < b[order] ? -1 : 0))
              .filter((item) => {
                return item.name.includes(searchText);
              })
              .map((recipe, i) => {
                return (
                  <li
                    onClick={() =>
                      changeRecipe({
                        ...recipe,
                        src: getObj(images, recipe.recipeId)
                          ? getObj(images, recipe.recipeId).imgUr
                          : null,
                      })
                    }
                  >
                    <Link to={`/products/${recipe.recipeId}`}>
                      <li>{recipe.name}</li>
                    </Link>
                  </li>
                );
              })}
        </ul>
        {!searchText && (
          <ul className="cards__items d-flex flex-wrap">
            <CardItem src="https://images.pexels.com/photos/372882/pexels-photo-372882.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" />
            <CardItem src="https://images.pexels.com/photos/372882/pexels-photo-372882.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" />
            <CardItem src="https://images.pexels.com/photos/372882/pexels-photo-372882.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" />
            <CardItem src="https://media.istockphoto.com/photos/traditional-ethiopian-dish-picture-id452732099?s=612x612" />
            <CardItem src="https://media.istockphoto.com/photos/traditional-ethiopian-dish-picture-id452732099?s=612x612" />
            <CardItem src="https://media.istockphoto.com/photos/traditional-ethiopian-dish-picture-id452732099?s=612x612" />
            <CardItem src="https://images.pexels.com/photos/7613432/pexels-photo-7613432.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" />
            <CardItem src="https://images.pexels.com/photos/372882/pexels-photo-372882.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" />
          </ul>
        )}
      </div>
    </div>
  );
}

export default SearchRecipes;
