import { useEffect, useState } from 'react'
import { getData } from '../DAL/api'
import { Multiselect } from 'multiselect-react-dropdown'
import { Row, Form, FormGroup } from 'react-bootstrap'
import * as Yup from 'yup'
const Search = () => {
    const [recipes, setRecipes] = useState([])
    const [ingredients, setIngredients] = useState([])
    const [selectedIngredients, setSelectedIngredients] = useState([])
    const [diets, setDiets] = useState([])
    const [selectedDiets, setSelectedDiets] = useState([])
    const [categories, setCategories] = useState([])
    const [selectedcategories, setSelectedcategories] = useState([])
    const [recipeText, setRecipeText] = useState('')

    useEffect(async () => {
        const getAllRecipes = await getData('recipes')
        const getIngredients = await getData('ingredients')
        const getCategories = await getData('categories')
        const getDiets = await getData('diets')
        setRecipes(getAllRecipes)
        setIngredients(getIngredients)
        setCategories(getCategories)
        setDiets(getDiets)
    }, [])

    const onSelcted1 = (data) => {
        console.log(selectedIngredients);
        setSelectedIngredients(data)


        // console.log(selectedIngredients);
        // setSelectedIngredients([...selectedIngredients, data])

    }
    return <div className='mt-5'>
        <h1>Multi Select</h1>
        <div>
            <Row className='d-flex container-fluid m-auto col-sm-4'>
                <div>
                    <Form.Group className="mb-3" controlId="formBasicEmail">
                        <Form.Label>recipe name</Form.Label>
                        <Form.Control type="email" placeholder="search recipe" />
                    </Form.Group>
                </div>
                <div>
                    <Form.Label>Ingredients</Form.Label>
                    {ingredients.length && <Multiselect options={ingredients}
                        displayValue={'name'} onSelect={onSelcted1}
                        placeholder='search ingredient' />}
                </div>
                <div>
                    <Form.Label>Category</Form.Label>
                    {categories.length && <Multiselect options={categories}
                        displayValue={'name'}
                        placeholder='category' />}
                </div>
                <div>
                    <Form.Label>diet types</Form.Label>
                    {diets.length && <Multiselect options={diets}
                        displayValue={'name'} placeholder='diet type' />}
                </div>
            </Row>
            <Row className='col-sm-6'>
            {newRecipes.length > 0 && newRecipes.map((recipe, i) => (

<div class="recipe">
  <div class="image">
    <img src={recipe.images[0] ? `http://localhost:3200/${recipe.images[0].url} ` : 'https://images.pexels.com/photos/3843282/pexels-photo-3843282.jpeg?auto=compress&cs=tinysrgb&dpr=3&h=750&w=1260'} />
    <div class="likes text-danger" onClick={(e) => {
      addToLikes(Cookies.get("user"), recipe.id)
    }}>
      <i class="fas fa-heart"></i>
    </div>
    <div class="name">
      <h3>{recipe.name}</h3>
    </div>
  </div>

  <ul class="media">
    <li><i class="fa fa-clock-o"></i> {recipe.prepTimeMin} <i class="far fa-clock"></i></li>
    <li><i class="fa fa-eye"></i> {recipe.views}</li>
    <li>  <i class="far fa-calendar-alt"></i> {(recipe.createdAt).slice(5, 10).split('-').reverse().join('-')}</li>
    <li> <Link to={`products/${recipe.id}`}>   <i class="fas fa-info-circle"></i></Link > </li>
  </ul>
</div>

))
}



            </Row>
        </div>

    </div>


}
export default Search;
