import { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import ShowAllRecipes from "../sections/ShowAllRecipes";
const { getData, getUserRecipes } = require('../DAL/api')


function Profile() {
    const history = useHistory()
    const [isLoged, setIsLoged] = useState(false)
    const [recipes, setRecipes] = useState([])
    const [images, setImages] = useState([])


    useEffect(async () => {
        const fetchImages = await getData('images')
        setImages(fetchImages)
        const user = localStorage.getItem('user')
        if (user) setIsLoged(true)

        const fetchRecipes = await getUserRecipes(user)
        setRecipes(fetchRecipes)
        if (isLoged) {
            history.push('/login')
        }

    }, [])


    return (

        isLoged && <ShowAllRecipes recipes={recipes} images={images} />
    )
}
export default Profile
