import { useState, useEffect } from 'react';
import { userRecipes, getFavorites } from "../DAL/api";
const Cookies = require('js-cookie')
const MyFovorites = () => {
    const [recipes, setRecipes] = useState([])

    useEffect(async () => {
        const id = Cookies.get("user")
        const favorites = await getFavorites(id)
        console.log(favorites);
        setRecipes(favorites)
    }, [])





    return <div className='mt-5'>


        {
            recipes.length && recipes.map(item => {
                <div class="recipe">
                    <div class="image">
                        <img src={item.recipe.images[0] ? `http://localhost:3200/${recipe.images[0].url} ` : 'https://images.pexels.com/photos/3843282/pexels-photo-3843282.jpeg?auto=compress&cs=tinysrgb&dpr=3&h=750&w=1260'} />
                        <div class="likes" onClick={(e) => {
                           
                        }}>
                            <i class="fas fa-heart"></i>
                        </div>
                        <div class="name">
                            <h3>{item.recipe.name}</h3>
                        </div>
                    </div>

                    <ul class="media">
                        <li><i class="fa fa-clock-o"></i> {recipe.prepTimeMin} <i class="far fa-clock"></i></li>
                        <li><i class="fa fa-eye"></i> {recipe.views}</li>
                        <li>  <i class="far fa-calendar-alt"></i> {(recipe.createdAt).slice(5, 10).split('-').reverse().join('-')}</li>
                        <li> <Link to={`products/${recipe.id}`}>   <i class="fas fa-info-circle"></i></Link > </li>
                    </ul>
                </div>


                return <li>{item.recipe.name}</li>

            })
        }


    </div>
}
export default MyFovorites
