import axios from 'axios'
import { useState } from 'react'

function Form() {
    const [image, setImage] = useState('')
    const handleChange = (e) => {
        setImage(e.target.files)

    }

    const handleSubmit = async (e) => {
        e.preventDefault()
        const data = new FormData()
        console.log(data);
        data.append('images', image)
        const response = await axios.post("http://localhost:3200/images", data)
        console.log(response);
    }
    console.log(image);
    return <form className='form-inner' encType="multipart/form-data" methiod="POST" action='/images/mult'>
        <div className='form-inner'>
            <input type="file" name='images'  accept='.jpg,.png' onChange={(e) => handleChange(e)} />
            <button type="submit" onClick={(e) => handleSubmit(e)} >upload</button>
        </div>

    </form>
}
export default Form
