import { useEffect, useState } from "react";
import { Row, Form } from "react-bootstrap";
import validateInfo from "../utills/validateInfo";
import { getUsers, login } from "../DAL/api";
import { Link } from "react-router-dom";

function Login() {
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const [users, setUsers] = useState([]);


  function changeInfo(e) {
    if (e.target.name == "email") setEmail(e.target.value);
    if (e.target.name == "password") setPassword(e.target.value);
  }
  const login = (email, password) => {
    let isLoged = false;
    return (email == 'lala@wawa.com' && password === 'kishkush12')
  };

  function onSubmit(e) {
    e.preventDefault();
    if (login(email, password)) {
      localStorage.setItem('name', 'no');
      alert("welcome");
    } else setMessage("something wrong try again");
  }

  useEffect(async () => {
    const users = await getUsers();
    setUsers(users);
  });

  return (
    <Form className="form-login">
      <div className="form-inner my-5 ">
        <h1>Login</h1>

        <label htmlFor="email" className="d-block">
          Email
        </label>
        {message && <small>{message}</small>}
        <input
          id="email"
          name="email"
          type="text"
          className="form-control"
          onChange={(e) => {
            changeInfo(e);
          }}
          defaultValue={email}
        />
        <label htmlFor="email">Password</label>
        <input
          id="password"
          name="password"
          type="text"
          className="form-control"
          onChange={(e) => {
            changeInfo(e);
          }}
          aria-describedby="emailHelp"
          defaultValue={password}
        />
        <Row>
          <button
            className="btn btn-primary my-2 text-right font-weight-bold "
            type="submit"
            onClick={onSubmit}
          >
            Login
          </button>
          <div className="d-flex m-auto">
            <Link to="/" className="btn btn-primary my-2 mx-3 col-sm-6">
              Cancel
            </Link>
            <Link to="/sign-up" className='row-sm-9'> Create New Acount </Link>
          </div>
        </Row>
      </div>
    </Form>
  );
}
export default Login;
