import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Spinner } from "react-bootstrap";

import CardItem from "./CardItem";
import { Form, Row, Col } from "react-bootstrap";
const ShowAllRecipes = ({ recipes, loading }) => {
  const [order, setOrder] = useState("color");
  if (loading) {
    return (
      <h2>
        טוען
        <Spinner animation="border" />
      </h2>
    );
  }

  

  return (
    <ul className=" d-flex flex-wrap justify-content-center">
      {recipes
        .sort((a, b) => (a[order] < b[order] ? -1 : 0))
        .map((recipe) => (
          <CardItem
            path={`/products/${recipe.recipeId}`}
            src="https://images.pexels.com/photos/372882/pexels-photo-372882.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"
            text={recipe.name}
          />
        ))}
    </ul>
  );
};

export default ShowAllRecipes;
