import React, { useEffect, useState } from "react";
import "./registerform.css";
import { Link } from "react-router-dom";
import { Form, Col, Button, Row } from "react-bootstrap";
import InputCreator from "./InputCreator";
import validate from "../utills/validateInfo";
import useForm from "../utills/useForm";
import ErrorMessages from "../utills/ErrorMessages";
import axios from 'axios'
import { getDiets } from "../DAL/api";

const RegisterForm = ({ recipe, editUser }) => {
  const [editMode, setEditMode] = useState(false);
  const [diets, setDiets] = useState([]);

  useEffect(async () => {
    if (recipe) setEditMode(true);
    const fetchDiets = await getDiets()
    setDiets(fetchDiets.data);
  }, []);

  const { handleSubmit, handleChange, values, errors } = useForm(
    submit,
    validate
  );


  function submit(e) {
   // e.preventDefault()
    console.log('im here');
    axios.post('http://localhost:3200/users/insert', {
      values
    }).then(() => console.log('success'))
    if (editMode) {
      editUser(values);
    }
  }

  return (
    <>
      <Form dir="rtl" onSubmit={handleSubmit}>
        <div className="form-inner">
          <Form.Row className="m-auto rtl">
            <h1 className="text-center">Registration page</h1>
          </Form.Row>
          <hr />
          <Form.Group>
            <InputCreator
              title="username"
              type="text"
              required="true"
              value={recipe ? (values.name = recipe.name) : values.name}
              placeholder="username"
              sign={<i class="fas fa-user"></i>}
              onChange={handleChange}
              name="username"
              error={errors.username}
            />
            {errors.username && <ErrorMessages errors={errors.username} />}
          </Form.Group>
          <Form.Group controlId="formGridPassword">
            <InputCreator
              title="Email"
              type="email"
              required="true"
              value={values.email}
              placeholder="Email"
              sign={<i class="fas fa-mail-bulk"></i>}
              onChange={handleChange}
              name="email"
              error={errors.email}
            />
            {errors.email && <ErrorMessages errors={errors.email} />}
          </Form.Group>
          <Form.Group>
            <InputCreator
              title="password"
              type="password"
              required="true"
              value={values.password}
              placeholder="password"
              sign={<i class="fas fa-mail-bulk"></i>}
              onChange={handleChange}
              name="password"
              error={errors.password}
              note="Minimum password requirements: at least a digit, 
              lowercase and uppercase letter in English. And no less than 8 characters"
            />
            {errors.password && <ErrorMessages errors={errors.password} />}
          </Form.Group>
          <Form.Group>
            <InputCreator
              title="confirm password"
              type="password"
              required="true"
              value={values.password2}
              placeholder=""
              sign={<i class="fas fa-mail-bulk"></i>}
              onChange={handleChange}
              name="password2"
              error={errors.password2}
              note="The content of the field will be the same as the content typed for the password"
            />
            {errors.password2 && <ErrorMessages errors={errors.password2} />}
          </Form.Group>
          <Form.Group>
            <Row>
              <Col>
                <label className='form-label text-left d-block'>favorite Category</label>
              </Col>
              <Col className="d-flex flex-row flex-wrap col-sm-10">
                {diets.length &&
                  diets.map((diet) => {
                    return (
                      <Form.Check
                        type="checkbox"
                        label={diet.name}
                        name={'diet'}
                        value={diet.id}
                        onChange={handleChange}
                      />
                    );
                  })}
                {errors.diet && <ErrorMessages errors={errors.diet} />}
              </Col>
            </Row>
          </Form.Group>

          {
            <Button type="submit" id="submit" onClick={handleSubmit}>
              {editMode ? "update" : "sign-up"}
            </Button>
          }

          <Link to="/login">have an acount?login</Link>
        </div>
      </Form>
    </>
  );
};

export default RegisterForm;
