import React, { useState, useEffect } from "react";
import demoData from "./demoData";
import CardItem from "../../CardItem";
import { Form, Col, Row } from "react-bootstrap";
import MyVerticallyCenteredModal from "../Modal";
import Pagination from "../Pagination";
function SearchRecipes() {
  const [searchText, setSearchText] = useState("");
  const [data, setData] = useState([]);
  const [order, setOrder] = useState("color");
  const [modalShow, setModalShow] = useState(false);
  const [selecteduser, setSelectedUser] = useState({});
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage] = useState(5);

  function changeUser(user) {
    setSelectedUser(user);
    setModalShow(true);
  }

  // handle change event of search input
  const handleChange = (value) => {
    setSearchText(value);
   
  };

  // filter records by search text
  // const filterData = (searchText) => {
  //   const lowercasedValue = searchText.toLowerCase().trim();
  //   if (lowercasedValue === "") setData(data);
  //   else {
  //     const filteredData = data
  //       .sort((a, b) => (a[order] < b[order] ? -1 : 0))
  //       .filter((item) => {
  //         return item.name.includes(searchText);
  //       });
  //     setData(filteredData);
  //   }
  // };

  // const indexOfLastPost = currentPage * postsPerPage;
  // const indexOfFirstPost = indexOfLastPost - postsPerPage;
  // const currentPosts = [...data];
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  useEffect(async () => {
    const fetchedData = await demoData();
    setData(fetchedData);
  }, []);
  useEffect(() => {
    // filterData(searchText);
    console.log(data);
  }, [order, searchText]);
  return (
    <div>
      <Row className="container-fluid App mx-auto">
        <Col>
          חיפוש:{" "}
          <input
            style={{ marginLeft: 5 }}
            type="text"
            placeholder="חפש מתכונים..."
            value={searchText}
            onChange={(e) => handleChange(e.target.value)}
          />
        </Col>
        <Col className="col-sm-2">
          <small>מיין לפי:</small>
          <Form.Check
            type="radio"
            label="תאריך"
            value={"year"}
            name="public"
            onChange={() => {
              setOrder("year");
            }}
          />
          <Form.Check
            type="radio"
            value={"name"}
            label="שם"
            name="public"
            onChange={(e) => {
              console.log("name");
              setOrder("name");
            }}
          />
          <Form.Check
            type="radio"
            value={"views"}
            label="צפיות"
            name="public"
            onChange={(e) => {
              console.log("name");
              setOrder("views");
            }}
          />
        </Col>
      </Row>
      <MyVerticallyCenteredModal
        show={modalShow}
        user={selecteduser}
        onHide={() => setModalShow(false)}
      />
      <div className="box-container d-flex flex-wrap text-center">
        {searchText &&
        data
        .sort((a, b) => (a[order] < b[order] ? -1 : 0))
        .filter((item) => {
          return item.name.includes(searchText);
        }).map((d, i) => {
            return (
              <span onClick={() => changeUser(d)} className="m-auto">
                <CardItem
                  text={d.name}
                  label={d.year}
                  views={d.views}
                  src={d.src}
                  key={i}
                  onClick={changeUser}
                  user={d}
                />
              </span>
            );
          })}

        <div className="clearboth"></div>
        {data.length === 0 && <span>לא נמצא</span>}
      </div>
    </div>
  );
}

export default SearchRecipes;
