import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Spinner } from "react-bootstrap";

import CardItem from "./CardItem";
import { Form, Row, Col } from "react-bootstrap";
const ShowAllRecipes = ({ recipes, loading, images }) => {
  const [order, setOrder] = useState("color");
  const [newImages, setNewImages] = useState([]);
  console.log("images", images);
  console.log("recipes", recipes);
  useEffect(() => {
    setNewImages(images);
  }, []);
  if (loading) {
    return (
      <h2>
        טוען
        <Spinner animation="border" />
      </h2>
    );
  }
  function getObj(array, filter) {
    console.log(array.find((item) => item.recipeId == filter));
    return array.find((item) => item.recipeId == filter)
      ? array.find((item) => item.recipeId == filter).imgUrl
      : undefined;
  }
  return (
    <>
      <Row className="container-fluid mx-auto">
        <Row>
          <Col className="col-sm-2 d-flex">
            <h6 className="d-block mx-3">מיין לפי:</h6>
            <Form.Check
              type="radio"
              label="תאריך"
              value={"year"}
              name="public"
              onChange={() => {
                setOrder("year");
              }}
            />
            <Form.Check
              type="radio"
              value={"name"}
              label="שם"
              name="public"
              onChange={(e) => {
                setOrder("name");
              }}
            />
            <Form.Check
              type="radio"
              value={"views"}
              label="צפיות"
              name="public"
              onChange={(e) => {
                setOrder("views");
              }}
            />
          </Col>
        </Row>
      </Row>
      <ul className=" d-flex flex-wrap justify-content-center">
        {recipes.map((recipe) => (
          <CardItem
            path={`/products/${recipe.recipeId}`}
            src={
              getObj(images, recipe.recipeId) ||
              "https://images.pexels.com/photos/4735285/pexels-photo-4735285.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"
            }
            //src=
            text={recipe.name}
            views={recipe.views}
            labe
          />
        ))}
      </ul>
    </>
  );
};

export default ShowAllRecipes;
