import React, { useEffect, useState } from "react";
import "./registerform.css";
import { Link, useHistory } from "react-router-dom";
import { Form, Col, Button, Row } from "react-bootstrap";
import InputCreator from "./InputCreator";
import validate from "../utills/validateInfo";
import useForm from "../utills/useForm";
import ErrorMessages from "../utills/ErrorMessages";
import { getData, insertDataToDB } from "../DAL/api";
import * as Yup from 'yup'
import { useFormik } from "formik";
const RegisterForm = ({ recipe, editUser }) => {
  const [editMode, setEditMode] = useState(false);
  const [diets, setDiets] = useState([]);
  const [meesages, setMessages] = useState('')
  const history = useHistory()
  useEffect(async () => {
    if (recipe) setEditMode(true);
    const fetchDiets = await getData('diets')
    console.log(fetchDiets);
    setDiets(fetchDiets);
  }, []);





  const formik = useFormik({
    initialValues: {
      firstName: "",
      lastName: "",
      email: "",
      password: "",
      diet: [],
      confirmPassword: '',
      description: '',
      image: ''
    },

    validationSchema: Yup.object({
      firstName: Yup.string()
        .max(15, 'Must be 15 characters or less')
        .required('Required'),
      lastName: Yup.string()
        .max(20, 'Must be 20 characters or less')
        .required('Required'),
      email: Yup.string()
        .email('Email is invalid')
        .required('Email is required'),
      password: Yup.string()
        .min(6, 'Password must be at least 6 charaters')
        .required('Password is required'),
      confirmPassword: Yup.string()
        .oneOf([Yup.ref('password'), null], 'Password must match')
        .required('Confirm password is required'),
      diet: Yup.array().required('required')
        .min(2, 'must check 2 types')
    })
    ,
    onSubmit: async (values) => {
      const data = new FormData();
     
      if (recipeId) formik.values.id = recipeId
      data.append("image", image)
      data.append("user", Cookies.get('user'))
      data.append("recipe", JSON.stringify(formik.values))
      if (!recipeId) {
        const response = await uploadRecipe(data)
      } else {
        const update = await updateRecipe(data)
      }
      submit()
    },

  })


  async function submit(e) {
    // console.log(objectIsEmpty(errors));
    // if (!objectIsEmpty(errors)) {
    const res = await insertDataToDB('users', values)
    console.log(res);
    // console.log(res);
    // history.push('/login')
    console.log(res);
    setMessages(res)
    if (editMode) {
      editUser(values);
    }
  }

  return (
    <>
      <Form dir="rtl" onSubmit={handleSubmit}>
        <div className="form-inner">
          <Form.Row className="m-auto rtl">
            <h1 className="text-center">Registration page</h1>
          </Form.Row>
          <hr />
          <Form.Group>
            <InputCreator
              title="First name"
              type="text"
              required="true"
              value={values.firstName}
              placeholder="First Name"
              sign={<i class="fas fa-user"></i>}
              onChange={formik.handleChange}
              name="firstName"
            />
            {formik.touched.firstName && formik.errors.firstName ? <p className='text-danger'>{formik.errors.firstName} </p> : ""}

          </Form.Group>
          <Form.Group>
            <InputCreator
              title="Last name"
              type="text"
              required="true"
              value={values.lastName}
              placeholder="last Name"
              sign={<i class="fas fa-user"></i>}
              onChange={formik.handleChange}
              name="lastName"
            />
            {formik.touched.firstName && formik.errors.firstName ? <p className='text-danger'>{formik.errors.firstName} </p> : ""}
          </Form.Group>
          <Form.Group controlId="formGridPassword">
            <InputCreator
              title="Email"
              type="email"
              required="true"
              onChange={formik.handleChange}
              placeholder="Email"
              sign={<i class="fas fa-mail-bulk"></i>}
              onChange={formik.handleChange}
              name="email"

            />
            {formik.touched.firstName && formik.errors.firstName ? <p className='text-danger'>{formik.errors.firstName} </p> : ""}
          </Form.Group>
          <Form.Group>
            <InputCreator
              title="Password"
              type="password"
              required="true"
              value={values.password}
              placeholder="password"
              sign={<i class="fas fa-mail-bulk"></i>}
              onChange={formik.handleChange}
              name="password"
                           note="Minimum password requirements: at least a digit, 
              lowercase and uppercase letter in English. And no less than 8 characters"
            />
            {formik.touched.firstName && formik.errors.firstName ? <p className='text-danger'>{formik.errors.firstName} </p> : ""}

          </Form.Group>
          <Form.Group>
            <InputCreator
              title="Confirm password"
              type="password"
              required="true"
              value={values}
              placeholder=""
              sign={<i class="fas fa-mail-bulk"></i>}
              onChange={formik.handleChange}
              name="password2"
              error={errors.password2}
              note="The content of the field will be the same as the content typed for the password"
            />
            {errors.password2 && <ErrorMessages errors={errors.password2} />}
          </Form.Group>
          <Form.Group>
            <Row>
              <Col>
                <label className='form-label text-left d-block'>Favorite diet</label>
              </Col>
              <Col className="d-flex flex-row flex-wrap col-sm-10">
                {diets.length &&
                  diets.map((diet) => {
                    return (
                      <Form.Check
                        type="checkbox"
                        label={diet.name}
                        name={'diet'}
                        value={diet.id}
                        onChange={formik.handleChange}
                      />
                    );
                  })}
                {errors.diet && <ErrorMessages errors={errors.diet} />}
              </Col>
            </Row>
          </Form.Group>
          {
            <Button type="submit" id="submit" onClick={handleSubmit}>
              {editMode ? "update" : "sign-up"}
            </Button>
          }
          <Link to="/login">have an acount?login</Link>
        </div>
      </Form>
    </>
  );
};

export default RegisterForm;
