import { useEffect, useState } from "react";
import { Row, Form } from "react-bootstrap";
import validateInfo from "../utills/validateInfo";
import { getUsers, login } from "../DAL/api";
import { Link } from "react-router-dom";

function Login() {
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const [users, setUsers] = useState([]);


  function changeInfo(e) {
    if (e.target.name == "email") setEmail(e.target.value);
    if (e.target.name == "password") setPassword(e.target.value);
  }
  const login = (email, password) => {
    return users.find(
      (user) => user.email === email && user.password == password
    );
  };

  function onSubmit(e) {
    e.preventDefault();
    if (!!login(email, password)) {
      setMessage("");
      alert("welcome");
    } else setMessage("טעות בפרטים, אנא נסה שוב");
  }

  useEffect(async () => {
    const users = await getUsers();
    setUsers(users);
  });

  return (
    <Form className="form-login">
      <div className="form-inner my-5 ">
        <h1>כניסה</h1>

        <label htmlFor="email" className="d-block">
          אימייל
        </label>
        {message && <small>{message}</small>}
        <input
          id="email"
          name="email"
          type="text"
          className="form-control"
          onChange={(e) => {
            changeInfo(e);
          }}
          defaultValue={email}
        />
        <label htmlFor="email">סיסמא</label>
        <input
          id="password"
          name="password"
          type="text"
          className="form-control"
          onChange={(e) => {
            changeInfo(e);
          }}
          aria-describedby="emailHelp"
          defaultValue={password}
        />
        <Row>
          <button
            className="btn btn-primary my-2 text-right font-weight-bold "
            type="submit"
            onClick={onSubmit}
          >
            כניסה
          </button>
          <Link to="/" className="btn btn-primary my-2 mx-3 ">
            ביטול
          </Link>
        </Row>
        <Row>
          <Link to="/sign-up"> אין משתמש?לחץ להרשמה</Link>
        </Row>
      </div>
    </Form>
  );
}
export default Login;
