import { useEffect, useState } from "react";
import { Row, Form } from "react-bootstrap";
import validateInfo from "../utills/validateInfo";
import { getUsers, login } from "../DAL/api";
import { Link } from "react-router-dom";
import { useHistory } from "react-router-dom";
import AuthApi from "../context/AuthApi";
import axios from "axios";
function Login() {
 
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const [users, setUsers] = useState([]);
  const history = useHistory()
  const Auth=use
  function changeInfo(e) {
    if (e.target.name === "email") setEmail(e.target.value);
    if (e.target.name === "password") setPassword(e.target.value);
  }
  // const login = (email, password) => {
  //   console.log(users);
  //   let isLoged = false;

  //   return users.find(user => (user.email == email && user.password == password))
  // };

  const onSubmit = async (e) => {
    e.preventDefault();
    const response = await login(email, password)
    console.log('response', response);
    if (response.length) {
      console.log('getaneh', response);
      localStorage.setItem('user', response[0].userId);
      console.log(localStorage.getItem('user'));
      history.push('/profile')
    }
    else {
      setMessage("something wrong try again");
    }
    // if (login(email, password)) {
    //   localStorage.setItem('name', login(email, password).userId);
    //   history.push('/profile')
    // } else setMessage("something wrong try again");
  }

  useEffect(async () => {
    if (localStorage.getItem('user')) history.push('/profile')
  }, []);

  return (
    <Form className="form-login" onSubmit={onSubmit}>
      <div className="form-inner my-5 ">
        <h1>Login</h1>

        <label htmlFor="email" className="d-block">
          Email
        </label>
        {message && <small>{message}</small>}
        <input
          id="email"
          name="email"
          type="text"
          className="form-control"
          onChange={(e) => {
            changeInfo(e);
          }}
          defaultValue={email}
        />
        <label htmlFor="email">Password</label>
        <input
          id="password"
          name="password"
          type="password"
          className="form-control"
          onChange={(e) => {
            changeInfo(e);
          }}
          aria-describedby="emailHelp"
          defaultValue={password}
        />
        <Row>
          <button
            className="btn btn-primary my-2 text-right font-weight-bold "
            type="submit"

          >
            Login
          </button>
          <div className="d-flex m-auto">
            <Link to="/" className="btn btn-primary my-2 mx-3 col-sm-6">
              Cancel
            </Link>
            <Link to="/sign-up" className='row-sm-9'> Create New Acount </Link>
          </div>
        </Row>
      </div>
    </Form>
  );
}
export default Login;
