import { useEffect, useState } from "react";
import { Modal, ListGroupItem, Button, Image } from "react-bootstrap";
import { Carousel } from "react-bootstrap";
import { Link } from "react-router-dom";
import "./Carousel.css";
import "./ItemDetails.css";
import { getRecipeIngredients, getData, getRecipeDetails, incrementView } from "../DAL/api";
const fetchData = require('../DAL/api')
const Cookies = require('js-cookie')
function ItemDetails(props) {
  const { match, product, onAdd } = props
  const [item, setItem] = useState([]);
  const [instructions, setInstructions] = useState([]);
  const [ingredients, setIngredients] = useState([]);
  const [userId, setUserId] = useState(0)


  useEffect(async () => {
    console.log('match', match.params.id);
    const recipeItem = await getRecipeDetails(match.params.id)
    console.log(recipeItem);
    setItem(recipeItem);
    setInstructions(recipeItem.instructions)
    setIngredients(recipeItem.recipeingredients)
    console.log();
    if (Cookies.get('user')) {
      const id = Cookies.get('user')
      console.log('id', id);
      setUserId(id)
    }

    const incView = await incrementView(match.params.id)
    console.log('increment', incView);
  }, []);
  console.log(item.category);
  return (
    <div className="form-inner product">
      <Carousel fade>
        {item.images &&
          item.images.map((image, i) => (
            <Carousel.Item key={i}>
              <img src={`http://localhost:3200/${image.url}`} alt="1" />
              <Carousel.Caption>
                <h3 className="text-center">{item.name}</h3>
                <p>{item.description}</p>
              </Carousel.Caption>
            </Carousel.Item>
          ))}
      </Carousel>
      {item.recipe && <div>
        <h2 class="recipe-title">{item.recipe.name}</h2>
        <p class="recipe-category">prep time: {item.recipe.prepTimeMin} minutes</p>
        {/* <ListGroupItem className='recipe-title'> name: {item.recipe.name}</ListGroupItem>
        <ListGroupItem className='recipe-category'> prep time: {item.recipe.prepTimeMin} minutes</ListGroupItem>
        <ListGroupItem>views: {item.recipe.views} </ListGroupItem> */}
        <div class="recipe-instruct">
          <h3>Instructions:</h3>
          {item.instructions.map((inst, i) => {
            return <p key={i}>{inst.instruction}</p>
          })}
        </div>
        <div class="recipe-instruct">
          <h3>ingredints:</h3>
          {item.ingredients && item.ingredients.map((inst, i) => {
            return <p key={i}>{inst.quantity}  {inst.unit}  {inst.name}</p>
          })}
        </div>
        <div class="recipe-instruct">
          <h3>Categories:</h3>
          {item.ingredients && item.category.map((inst, i) => {
            return <p key={i}>{inst.name}</p>
          })}
        </div>
        <div class="recipe-instruct">
          <h3>Diets:</h3>
          {item.ingredients && item.diet.map((inst, i) => {
            return <p key={i}>{inst.name}</p>
          })}
        </div>


        {/* <ListGroupItem
          data-toggle="collapse"
          data-target="#collapseExample"
          aria-expanded="false"
          aria-controls="collapseExample"
        >
          ingredients-click to see
        </ListGroupItem>
        <ListGroupItem
          data-toggle="collapse"
          data-target="#collapseExample1"
          aria-expanded="false"
          aria-controls="collapseExample1"
        >
          instrictions-click to see
        </ListGroupItem> */}

        {/* <div className="collapse" id="collapseExample">
          <div class="card card-body">
            {item.ingredients && item.ingredients.map((inst, i) => {

              return <li key={i}>{inst.quantity} | {inst.unit} | {inst.name}</li>
            })}
          </div>
        </div>
        <div className="recipe-instruct" id="collapseExample1">

          {item.instructions.map((inst, i) => {

            return <li key={i}>{inst.instruction} | </li>
          })}
        </div> */}
      </div>
      }
      <div>
        {item.userId === userId && <Link className="btn btn-primary m-2" to={`/edit/${item.recipeId}`}>
          Edit
        </Link>}
        <Link className="btn btn-primary m-2" to='/products'>
          Go back
        </Link>
      </div>
    </div >
  );
}

export default ItemDetails;
