import { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import ShowAllRecipes from "../sections/ShowAllRecipes";
import CardItem from "../sections/CardItem";
const { getData, getUserRecipes } = require('../DAL/api')


function Profile() {
    const history = useHistory()
    const [isLoged, setIsLoged] = useState(false)
    const [recipes, setRecipes] = useState([])
    const [images, setImages] = useState([])


    useEffect(async () => {

       
        const user = localStorage.getItem('user')
        if (user) setIsLoged(true)
        console.log(user);
        let recipeId;
        const fetchRecipes = await getData('recipes', user)
        setRecipes(fetchRecipes)
        if(fetchRecipes) recipe
        const fetchImages = await getData('images', user)
        setImages(fetchImages)
       
        if (isLoged) {
            history.push('/login')
        }
    }, [])

    function getObj(array, filter) {
        const image = array.find((item) => item.recipeId == filter)
        if (image) return image.imgUrl
    }
    return (

        isLoged && <ul className=" d-flex flex-wrap justify-content-center  ">
            {recipes.map((recipe) => (
                <CardItem
                    path={`/products/${recipe.recipeId}`}
                    src={
                        getObj(images, recipe.recipeId) ||
                        "https://images.pexels.com/photos/4735285/pexels-photo-4735285.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"
                    }
                    text={recipe.name}
                    views={recipe.views}
                    label={recipe.name}
                  
                />
            ))}
        </ul>
    )
}
export default Profile
