import React from "react";
import { Link } from "react-router-dom";
import { Spinner } from "react-bootstrap";
import CardItem from "./CardItem";
const ShowAllRecipes = ({ recipes, loading }) => {
  if (loading) {
    return (
      <h2>
        טוען
        <Spinner animation="border" />
      </h2>
    );
  }


<Row className="container-fluid mx-auto">
        <Row>
          <Col>
            חיפוש:{" "}
            <input
              style={{ marginLeft: 5 }}
              type="text"
              placeholder="חפש מתכונים..."
              value={searchText}
              onChange={(e) => handleChange(e.target.value)}
            />
          </Col>
          <Col className="col-sm-2 d-flex">
            <h6 className="d-block mx-3">מיין לפי:</h6>
            <Form.Check
              type="radio"
              label="תאריך"
              value={"year"}
              name="public"
              onChange={() => {
                setOrder("year");
              }}
            />
            <Form.Check
              type="radio"
              value={"name"}
              label="שם"
              name="public"
              onChange={(e) => {
                setOrder("name");
              }}
            />
            <Form.Check
              type="radio"
              value={"views"}
              label="צפיות"
              name="public"
              onChange={(e) => {
                setOrder("views");
              }}
            />
          </Col>
        </Row>
      </Row>





  return (
    <ul className=" d-flex flex-wrap justify-content-center">
      {recipes.map((recipe) => (
        <CardItem
          path={`/products/${recipe.recipeId}`}
          src="https://images.pexels.com/photos/372882/pexels-photo-372882.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"
          text={recipe.name}
        />
      ))}
    </ul>
  );
};

export default ShowAllRecipes;
