import {
  Modal,
  Button,
  ListGroupItem,
  Image,
  Accordion,
  Card,
  Form,Col,Row
} from "react-bootstrap";
import "./Modal.css";

function MyVerticallyCenteredModal(props) {
  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          {props.user.username}
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Image
          id="modal-img"
          height="100 rem"
          style={{ margin: "1rem 5rem" }}
          variant="ml-5 text-center"
          src={props.user.src}
          rounded
        />
        <ListGroupItem> שם: {props.user.name}</ListGroupItem>
        <ListGroupItem> אופן ההכנה</ListGroupItem>
        <ListGroupItem> זמן הכנה: {props.user.grade}</ListGroupItem>
        <div>
         
      </div>
      </Modal.Body>
     
      <Modal.Footer>
        <Button onClick={props.onHide}>Close</Button>
      </Modal.Footer>
    </Modal>
  );
}

export default MyVerticallyCenteredModal;
