import { Modal, ListGroupItem, Button, Image } from "react-bootstrap";
import { Carousel } from "react-bootstrap";
import { Link } from "react-router-dom";
import Details from './Details'
import "./Carousel.css";
import "./ItemDetails.css";
import { getRecipeIngredients, getData, getRecipeDetails, incrementView } from "../DAL/api";
const fetchData = require('../DAL/api')
const Cookies = require('js-cookie')
function ItemDetails(props) {
  const { match } = props
  const [item, setItem] = useState([]);
  const [userId, setUserId] = useState(null)
  const [instructions, setInstructions] = useState([]);
  const [ingredients, setIngredients] = useState([]);
  useEffect(async () => {
    console.log('match', match.params.id);
    const recipeItem = await getRecipeDetails(match.params.id)
    console.log(recipeItem);
    setItem(recipeItem);
    setInstructions(recipeItem.instructions)
    setIngredients(recipeItem.recipeingredients)
    console.log();
    if (Cookies.get('user')) {
      const id = Cookies.get('user')
      setUserId(id)
      console.log('id', id);
      setUserId(id)
    }
    const incView = await incrementView(match.params.id)

  }, []);

  return (
    <>

      {item.recipe && item.recipe.map((recipe, i) => {
        return <div className='text-center'>
          <div className="row mx-2">
            <div className="col-sm-7">
              <h1>{recipe.name}</h1>
              <figure>
                <img src={`http://localhost:3200/${recipe.images[0]?.url}`} alt="1" />            <figcaption>Shania Pinnata</figcaption>
              </figure>
            </div>
            <div className='col-sm-4'>
              <p>This quick and delicious pasta dish is the perfect way to use up a summer bounty of basil and tomatoes! </p>
              <p>Don't have a green thumb? Never fear! You can knock out this classNameic pasta dish in no time with a jar of prepared pesto sauce. </p>
              <ul>
                <li>üçΩ Serves: 4</li>
                <li>‚è± Prep Time: 5 minutes</li>
                <li>üë®‚Äçüç≥ Cook Time: 25 minutes</li>
                <li>üçù Total Time: 30 minutes</li>
              </ul>
            </div>
          </div>
        </div>
      })
      }
    </>
  );
}

