import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Spinner } from "react-bootstrap";

import CardItem from "./CardItem";
import { Form, Row, Col } from "react-bootstrap";
import { getData } from "../DAL/api";
const ShowAllRecipes = (props) => {
  const { recipes, images, loading, onAdd } = props
  console.log(recipes);

  const [order, setOrder] = useState("color");
  const [newImages, setNewImages] = useState([]);


  if (loading) {
    return (
      <h2>
        Loading
        <Spinner animation="border" />
      </h2>
    );
  }
  const getObj = async (filter) => {
    const images = await getData('images', filter)
    console.log(images);
    
    if (images.length>0) return images[0].imgUrl
    else return;
  }
  return (
    <>
      <ul className=" d-flex flex-wrap justify-content-center  ">
        {recipes.map((recipe) => (
          <CardItem
            path={`/products/${recipe.recipeId}`}
            src={
             getObj(recipe.recipeId)
            }
            //src=
            text={recipe.name}
            views={recipe.views}
            label={recipe.name}
            onAdd={onAdd}
          />
        ))}
      </ul>
    </>
  );
};

export default ShowAllRecipes;
