import { useState } from "react";
import validateInfo from "./validateInfo";
import { login } from "./api.js";
function Login() {
  const [password, setPasseord] = useState("");
  const [email, setEmail] = useState("");
  function changePassword(e) {
    console.log("password");
    setPasseord(e.target.value);
  }
  function changeEmail(e) {
    console.log("email");
    setEmail(e.target.value);
  }
  function onSubmit(e) {
    e.preventDefault();
    if (login(email, password)) alert("Welcome");
    else alert("probably we dont you");
  }
  return (
    <div className="container w-25">
      <h1>כניסה</h1>
      <label htmlFor="email">אימייל</label>
      <input
        id="email"
        name="email"
        type="text"
        className="form-control"
        onChange={(e) => {
          changeEmail(e);
        }}
        defaultValue={email}
      />
      <label htmlFor="email">סיסמא</label>
      <input
        id="password"
        name="password"
        type="text"
        className="form-control"
        onChange={(e) => {
          changePassword(e);
        }}
        aria-describedby="emailHelp"
        defaultValue={password}
      />
      <button
        className="btn btn-primary my-2 text-right font-weight-bold "
        type="submit"
        onClick={onSubmit}
      >
        כניסה
      </button>
      <button
        className="btn btn-primary mx-3 text-right font-weight-bold "
        type="cancel"
        href="/"
      >
        ביטול
      </button>
      <a href="/sign-up">אין משתמש ?לחץ להרשמה </a>
    </div>
  );
}
export default Login;
