  }, [src])
  if (loading) {
    return (
      <h2>
        Loading
        <Spinner animation="border" />
      </h2>
    );
  }
  var src = '';
  const getObj = async (filter) => {
    const images = await getData('images', filter)
    console.log(images[0].imgUrl);
    if (images.length > 0) {
      src += (images[0].imgUrl)
    }
    else return []
    console.log('121');
  }

  return (
    <>
      <ul className=" d-flex flex-wrap justify-content-center  ">

        {recipes.map((recipe) => (

          < CardItem
            a={getObj(recipe.recipeId)}
            path={`/products/${recipe.recipeId}`}
            src={src}

            text={recipe.name}
            views={recipe.views}
            label={recipe.name}
            onAdd={onAdd}
          />

        ))}
      </ul>
    </>
  );
};

export default ShowAllRecipes;
