import { useEffect, useState } from "react";
import * as Yup from 'yup'
import { Accordion, Card, Button, Form, Col, Row, Table, } from "react-bootstrap";
import useForm from "../utills/useForm";
import validate from "../utills/validateInfo";
import { getData, insertDataToDB, ingredientsOfRecipe, uploadRecipe, appentIngredient, getRecipeDetails, updateRecipe } from "../DAL/api";
import { useHistory } from "react-router-dom";
import ErrorMessages from "../utills/ErrorMessages";
import { useFormik } from "formik";
const Cookies = require('js-cookie')
const axios = require('axios')


function NewRecipeForm({ match, recipeId, mode }) {
  const [ingredients, setIngredients] = useState([]);
  const [image, setImage] = useState(null)
  const [units, setUnits] = useState([]);
  const [instructions, setInstructions] = useState([]);
  const [guide, setGuide] = useState([]);
  const [diets, setDiets] = useState([]);
  const [recipeIngredient, setRecipeIngredient] = useState({})
  const [recipeGuide, setRecipeGuide] = useState({})
  const [editMode, setEditMode] = useState(false)
  const [newIngredient, setNewIngredient] = useState('')
  const history = useHistory()
  const [addIng, setAddIng] = useState(false)
  const [categories, setCategories] = useState([]);
  // const { handleSubmit, handleChange, values, errors } = useForm(submit, validate);




  const formik = useFormik({
    initialValues: {
      name: "",
      isPrivate: "",
      source: "",
      sourceUrl: "",
      diet: [],
      category: [],
      prepTimeMin: '',
      description: '',
      image: ''
    },

    validationSchema: Yup.object({
      name: Yup.string()
        .min(3, '')
        .max(15)
        .required('please enter'),
      isPrivate: Yup.string().min(1),
      // image: Yup.string().required("required"),
      diet: Yup.array().min(2, 'choose at least 2 types'),
      category: Yup.array().min(2, 'choose at least 2 types'),
      prepTimeMin: Yup.string(),
      description: Yup.string().min(3).max(30).required('3'),
      source: Yup.string().min(3, 'min 3 charachters').max(20, 'must be 20 charachters or less').required('required')
    }),
    onSubmit: async (values) => {
      const data = new FormData();
      formik.values.guide = guide;
      formik.values.userId = 95;
      formik.values.instructions = instructions
      if (recipeId) formik.values.id = recipeId
      data.append("image", image)
      data.append("user", Cookies.get('user'))
      data.append("recipe", JSON.stringify(formik.values))
      if (!recipeId) {
        const response = await uploadRecipe(data)
      } else {
        const update = await updateRecipe(data)
      }
      submit()
    },

  })


  const deleteInstruction = (index) => {
    instructions.splice(index, 1);
    setInstructions([...instructions]);
  };

  const deleteGuide = (index) => {
    guide.splice(index, 1);
    setGuide([...guide]);
  };
  const checkThefilds = (obj) => {
    if (Object.keys(obj).length === 0 || Object.keys(obj).length < 3) return false
    for (const prop in obj) {
      if (!obj[prop]) return false
    }
    return true
  }

  const addToList = (value) => {
    if (value === 'ingredients') {
      if (checkThefilds(recipeIngredient)) {
        setInstructions([...instructions, recipeIngredient])
      } else {
        alert('please check the fields')
      }
    }
    if (value === 'guide') setGuide([...guide, recipeGuide])
    else return
  }


  const insertDataToForm = (recipe) => {
    formik.values.name = recipe.recipe.name
    formik.values.source = recipe.recipe.source
    formik.values.sourceUrl = recipe.recipe.sourceUrl
    formik.values.description = recipe.recipe.description
    formik.values.isPrivate = !!recipe.recipe.isPrivate
    formik.values.prepTimeMin = recipe.recipe.prepTimeMin
    console.log(recipe.category);
    setImage(recipe.images[0]?.url)
    console.log(recipe.ingredients);
    setGuide(recipe.instructions)
    const arr = []
    for (let x of recipe.category) {
      console.log(x);
      arr.push(String(x.categoryId))
    }
    formik.values.category = arr
    const diets = []
    for (let x of recipe.diet) {

      diets.push(+(x.dietId))
    }
    formik.values.diet = diets;
  }

  async function submit() {
    // if (checkErrors(errors)) {
    const data = new FormData();
    formik.values.guide = guide;
    formik.values.instructions = instructions
    if (recipeId) formik.values.id = recipeId
    data.append("image", image)
    data.append("user", Cookies.get('user'))
    data.append("recipe", JSON.stringify(formik.values))
    if (!recipeId) {
      const response = await uploadRecipe(data)
    } else {
      const update = await updateRecipe(data)
    }
  }
  const changeIngredient = (e) => {
    const { name, value } = e.target;
    setRecipeIngredient({ ...recipeIngredient, [name]: value })
  }

  const changeGuide = (e) => {
    const { name, value } = e.target;
    setRecipeGuide({ ...recipeGuide, [name]: value })
  }

  const addIngtoDB = async () => {
    console.log(newIngredient);
    const response = await appentIngredient(newIngredient);
    setAddIng(false)
  }

  useEffect(async () => {
    if (!Cookies.get('user')) history.push('/login')
    const fetchIngredient = await getData('ingredients')
    const fetchDiets = await getData('diets')
    const fetchUnits = await getData('units')
    const fetchCategories = await getData('categories')
    setDiets(fetchDiets.map(item=>{
      
    }));
    setUnits(fetchUnits);
    setIngredients(fetchIngredient);
    setCategories(fetchCategories);
    if (recipeId) {
      setEditMode(true)
      const instruc = await ingredientsOfRecipe(recipeId)
      const formRecipe = await getRecipeDetails(recipeId)
      insertDataToForm(formRecipe)
      console.log("instruc", formRecipe);
      setInstructions(instruc)
    }
  }, []);

  return (
    <Form onSubmit={formik.handleSubmit} encType="multipart/form-data" >
      <div className="form-inner">
        <h1 className='text-center'>Adding new recipe</h1>
        <Row className="container">
          <Col className="col-md-3">
            <Form.Label>name </Form.Label>
            <Form.Control type="text" name="name" value={formik.values}{...formik.getFieldProps("name")} />
            {formik.touched.name && formik.errors.name ? <p className='text-danger'>{formik.errors.name} </p> : ""}
          </Col>
          <Col className="col-md-3 mr-4">
            <Row>
              <Form.Label as="Row" column sm={10}>
                view permition
              </Form.Label>
            </Row>
            <Row sm={10}>
              <Form.Check type="radio" label="public" name="isPrivate"
                value="1" onChange={formik.handleChange}
              />
              <Form.Check type="radio" label="private" name="isPrivate"
                value="0" onChange={formik.handleChange}
              />
              {formik.touched.isPrivate && formik.errors.isPrivate ? <p className='text-danger'>{formik.errors.isPrivate} </p> : ""}
            </Row>
          </Col>
          <Col>
            <Form.File label="main pic" name="image" multiple DefaultValue={formik.values}
              onChange={(e) => setImage(e.target.files[0])}
            />
            {formik.touched.image && formik.errors.image ? <p className='text-danger'>{formik.errors.image} </p> : ""}
          </Col>
        </Row>
        <Row className="container-fluid">
          <Col>
            <Form.Label> source</Form.Label>
            <Form.Control type="text" placeholder="recipe source" name="source" value={formik.values}{...formik.getFieldProps("source")}
            />
            {formik.touched.source && formik.errors.source ? <p className='text-danger'>{formik.errors.source} </p> : ""}

          </Col>
          <Col>
            <Form.Label> link to source </Form.Label>
            <Form.Control type="text" placeholder=" if exists" name="sourceUrl" value={formik.values} {...formik.getFieldProps("sourceUrl")}
            />
            {formik.touched.sourceUrl && formik.errors.sourceUrl ? <p className='text-danger'>{formik.errors.sourceUrl} </p> : ""}
          </Col>
        </Row>
        <Accordion defaultActiveKey="0">
          <Card>
            <Card.Header>
              <Accordion.Toggle as={Button} variant="link" eventKey="3">
                General description
              </Accordion.Toggle>
            </Card.Header>
            <Accordion.Collapse eventKey="3">
              <div>
                <Row>
                  <Col>
                    <Form.Label>
                      description
                    </Form.Label>
                    <Form.Control
                      as="textarea"
                      rows={2}
                      name="description"
                      placeholder='The description will appear below the title'
                      value={formik.values}
                      {...formik.getFieldProps("description")}
                    />
                    {formik.touched.description && formik.errors.description ? <p className='text-danger'>{formik.errors.description} </p> : ""}
                  </Col>
                  <Col className='col-sm-2'>
                    <Form.Label>
                      prepTime
                    </Form.Label>
                    <Form.Control
                      type="number"
                      min={3}
                      placeholder='time in minutes'
                      name="prepTimeMin"
                      value={formik.values} {...formik.getFieldProps("prepTimeMin")}
                    />
                    {formik.touched.prepTimeMin && formik.errors.prepTimeMin ? <p className='text-danger'>{formik.errors.prepTimeMin} </p> : ""}

                  </Col>
                </Row>
                <Row>
                  <Row className="container w-75 m-auto">
                    <Col>
                      <Row className="mr-3">diet type: </Row>
                      <Row>
                        {diets.length &&
                          diets.map((diet) => {
                            return (
                              <Form.Check
                                type="checkbox"
                                label={diet.name}
                                name='diet'
                                value={'' + diet.id}
                                onChange={(e) => {

                                  formik.handleChange(e)
                                  console.log(formik.values.diet)
                                }} />
                            );
                          })}
                        {formik.touched.diet && formik.errors.diet ? <p className='text-danger'>{formik.errors.diet} </p> : ""}
                      </Row>
                    </Col>
                    <Col>
                      <Row className="mr-3">category </Row>
                      <Row>
                        {categories.length &&
                          categories.map((category) => {
                            return (
                              <Form.Check
                                type="checkbox"
                                label={category.name}
                                name='category'
                                value={category.id}
                                onChange={formik.handleChange}
                              />

                            );
                          })}
                        {formik.touched.category && formik.errors.category ? <p className='text-danger'>{formik.errors.category} </p> : ""}
                      </Row>

                    </Col>
                  </Row>
                </Row>
              </div>
            </Accordion.Collapse>
          </Card>
          <Card>
            <Card.Header>
              <Accordion.Toggle as={Button} variant="link" eventKey="0">
                Ingredients
              </Accordion.Toggle>
            </Card.Header>
            <Accordion.Collapse eventKey="0">
              <>
                {instructions.length > 0 &&

                  instructions.map((item, i) => (
                    <div key={i} className='d-flex ingredints'>
                      <div className='col-sm-1'>{item.quantity}</div>
                      <div className='col-sm-3'>{units[item.measureUnit - 1].name}</div>
                      <div className='col-sm-3'>{ingredients[item.ingredient - 1].name}</div>
                      <div className='col-sm-4'>{item.note}</div>
                      <i class="fas fa-trash text-sm" onClick={() => deleteInstruction(i)}> </i>
                    </div>
                  ))}

                <Row className="p-5">
                  <Col className="col-sm-2">
                    <Form.Label>quantity</Form.Label>
                    <Form.Control type="number" min={1} name='quantity' onChange={(e) => { changeIngredient(e) }} />
                  </Col>
                  <Col className="col-md-4">
                    <Form.Label>measurement unit</Form.Label>
                    <select onChange={(e) => changeIngredient(e)} name='measureUnit'>
                      <option>---choose </option>
                      {units.map((unit, i) => {
                        return (
                          <option key={i} value={unit.id} name={unit.name}>
                            {unit.name}
                          </option>
                        );
                      })}
                    </select>
                  </Col>
                  <Col>
                    <Form.Label>Ingredients</Form.Label>
                    <select onChange={(e) => changeIngredient(e)} name='ingredient'>
                      <option>---choose </option>
                      {ingredients.length > 0 && ingredients.map((ingredient, i) => {
                        return (
                          <>
                            <option key={i} value={ingredient.id} name={ingredient.name}>
                              {ingredient.name}
                            </option>
                          </>
                        );
                      })}
                    </select>

                  </Col>
                  <Col className="col-md-2">
                    <Button onClick={() => setAddIng(true)}> add Ingredint</Button>

                  </Col>
                  <Row >
                    <Col >
                      <Button
                        className="md-6 mt-3"
                        onClick={(e) => addToList('ingredients')}>
                        Save
                      </Button>
                    </Col>
                  </Row>
                  {addIng && <div className='new-ing'>
                    <Form.Label>idgredint name</Form.Label>
                    <Form.Control type="text"
                      defailtValue={newIngredient}
                      name="newingredient" onChange={(e) => setNewIngredient(e.target.value)} />
                    <Button onClick={() => addIngtoDB()}>add</Button>
                  </div>}
                </Row>
              </>
            </Accordion.Collapse>
          </Card>
          <Card>
            <Card.Header>
              <Accordion.Toggle as={Button} variant="link" eventKey="1">
                Preparation steps
              </Accordion.Toggle>
            </Card.Header>
            <Accordion.Collapse eventKey="1">
              <>
                {guide.length > 0 &&
                  guide.map((item, i) => (
                    <div key={i} className='d-flex'>
                      <div className='col-sm-5'>{item.number}</div>
                      <div className='col-sm-5'>{item.instruction}</div>
                      <button onClick={() => deleteGuide(i)}
                        className='col-sm-1'>
                        <i class="fas fa-trash text-sm "></i>
                      </button>
                    </div>
                  ))}
                <Row className="d-flex p-5">
                  <Col className="col-md-10">
                    <Form.Label>instruction</Form.Label>
                    <Form.Control as="input" name='instruction' onChange={(e) => changeGuide(e)} />
                  </Col>
                  <Col className="col-md-2 m-auto">
                    <Button
                      className="m-2 flex-grow-0"
                      onClick={() => addToList('guide')}
                    >
                      Save
                    </Button>
                  </Col>
                </Row>
              </>
            </Accordion.Collapse>
          </Card>
        </Accordion>
        <Row className="text-left">
          <Button type="submit"  >
            {editMode ? "Edit Recipe" : "Add Recipe"}
          </Button>
        </Row>
      </div>
    </Form >
  );
}
export default NewRecipeForm;
