export default function validateInfo(values) {
  console.log(values);
  let errors = {};
  errors.username = [];
  errors.email = [];

  errors.adress = [];
  errors.password = [];
  errors.password2 = [];

  if (!values.username || !values.username.trim()) {

    errors.username.push("נא להכניס שם משתמש ");
  }
  if (values.username && values.username.length < 3) {
    errors.username.push("Username must be at least 3 characters long");
  }

  if (!values.email) {
    errors.email.push("נ");
  } else if (
    !/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(values.email)
  ) {
    errors.email.push("המייל שהוכנס אינו תקין");
  }

  if (!values.password) {
    errors.password.push("נא הכנס סיסמא");
  } else if (
    !/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[a-zA-Z\d]{8,}$/.test(values.password)
  ) {
    errors.password.push("הסיסמא לא תואמת לדרישות");
  }
  if (values.password != values.password2) {
    console.log(values.password2);
    errors.password2.push("הסיסמאות אינן זהות");
  }

  return errors;
}
