import React, { useState, useEffect } from "react";
import demoData from "./demoData";
import CardItem from "../../CardItem";
import { Form, Col, Row } from "react-bootstrap";
import MyVerticallyCenteredModal from "../Modal";
import { getRecipes, getImages } from "../../DAL/api";
import Pagination from "../Pagination";
import { Link } from "react-router-dom/cjs/react-router-dom.min";
import Cards from "../../Cards";
function SearchRecipes() {
  const [searchText, setSearchText] = useState("");
  const [images, setImages] = useState([]);
  const [data, setData] = useState([]);
  const [order, setOrder] = useState("color");
  const [modalShow, setModalShow] = useState(false);
  const [selecteduser, setSelectedUser] = useState({});
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage] = useState(5);

  function changeUser(user) {
    setSelectedUser(user);
    setModalShow(true);
  }
  function getObj(array, filter) {
    return array.find((item) => item.recipeId === filter);
  }
  console.log("getaneh", getObj(images, 2));
  // handle change event of search input
  const handleChange = (value) => {
    setSearchText(value);
    console.log("images", images);
  };

  // const indexOfLastPost = currentPage * postsPerPage;
  // const indexOfFirstPost = indexOfLastPost - postsPerPage;
  // const currentPosts = [...data];
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  useEffect(async () => {
    const fetchedImages = await getImages();
    const fetchedRecipes = await getRecipes();
    setData(fetchedRecipes);
    setImages(fetchedImages);
  }, []);

  useEffect(() => {}, [order, searchText]);

  return (
    <div>
      <Row className="container-fluid mx-auto">
        <Row>
          <Col>
            חיפוש:{" "}
            <input
              style={{ marginLeft: 5 }}
              type="text"
              placeholder="חפש מתכונים..."
              value={searchText}
              onChange={(e) => handleChange(e.target.value)}
            />
          </Col>
          <Col className="col-sm-2">
            <h6>מיין לפי:</h6>
            <Form.Check
              type="radio"
              label="תאריך"
              value={"year"}
              name="public"
              onChange={() => {
                setOrder("year");
              }}
            />
            <Form.Check
              type="radio"
              value={"name"}
              label="שם"
              name="public"
              onChange={(e) => {
                console.log("name");
                setOrder("name");
              }}
            />
            <Form.Check
              type="radio"
              value={"views"}
              label="צפיות"
              name="public"
              onChange={(e) => {
                console.log("name");
                setOrder("views");
              }}
            />
          </Col>
        </Row>
      </Row>
      <MyVerticallyCenteredModal
        show={modalShow}
        user={selecteduser}
        onHide={() => setModalShow(false)}
      />
      <div className="box-container  flex-wrap text-center">
        {searchText &&
          data
            .sort((a, b) => (a[order] < b[order] ? -1 : 0))
            .filter((item) => {
              return item.name.includes(searchText);
            })
            .map((d, i) => {
              return (
                <span
                  onClick={() =>
                    changeUser({
                      ...d,
                      src: getObj(images, d.recipeId)
                        ? getObj(images, d.recipeId).imgUrl
                        : null,
                    })
                  }
                  className="m-auto"
                >
                  <Link to={`/products/${d.recipeId}`}>
                    <li>{d.name}</li>
                  </Link>
                </span>
              );
            })}
        <ul className="cards__items d-flex flex-wrap">
          <CardItem src="https://media.istockphoto.com/photos/traditional-ethiopian-dish-picture-id452732099?s=612x612" />
          <CardItem
            src="https://media.istockphoto.com/photos/traditional-ethiopian-dish-picture-id452732099?s=612x612"
           
          />
          <CardItem
            src="https://media.istockphoto.com/photos/traditional-ethiopian-dish-picture-id452732099?s=612x612"
            
          />
          <CardItem
            src="https://media.istockphoto.com/photos/traditional-ethiopian-dish-picture-id452732099?s=612x612"
            text="תוכלו להינות ממתכונים מיוחדים  הישר מהמבטח האתיופי"
            label="מיוחדים"
            path="/sign-up"
          />
          <CardItem
            src="https://images.pexels.com/photos/7613432/pexels-photo-7613432.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"
            text="מתכוני בשר עם תיבולים עדינים ומדוייקים"
            label="בשרים"
            path="/sign-up"
          />
          <CardItem
            src="https://images.pexels.com/photos/372882/pexels-photo-372882.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500"
            text="    מתכונים בריאים וקלים להכנה. בזמן קצר ומט מצרכים"
            label="סלטים"
            path="/sign-up"
          />
        </ul>
        {data.length === 0 && <span>לא נמצא</span>}
      </div>
    </div>
  );
}

export default SearchRecipes;
