import { useEffect, useState } from "react";
import fetch from "../DAL/api";
import { Modal, ListGroupItem, Button, Image } from "react-bootstrap";
import { Carousel } from "react-bootstrap";
import { Link } from "react-router-dom";
import "./Carousel.css";
import "./ItemDetails.css";
import { getRecipeIngredients, getData } from "../DAL/api";
const fetchData = require('../DAL/api')

function ItemDetails(props) {
  const { match, product, onAdd } = props
  const [item, setItem] = useState([]);
  const [images, setImages] = useState([]);
  const [instructions, setInstructions] = useState([]);
  const [ingredients, setIngredients] = useState([]);
  const [indredientsName, setIngredientsName] = useState([])


  const [openEditForm, setOpenEditform] = useState(false);

  const addToCart = (item) => {
    console.log(ingredients);
    const ing = indredientsName.filter(item => ingredients.includes(item.name))
    console.log(ing);
    if (!JSON.parse(localStorage.getItem("newList"))) {
      localStorage.setItem("newList", JSON.stringify(ing))
    } else {
      const colors = [...JSON.parse(localStorage.getItem("newList")), ...ing];
      localStorage.setItem("newList", JSON.stringify(colors));
    }

    console.log(indredientsName);
  }
  useEffect(async () => {
    const imagesArray = [];
    const instructionsArray = []
    const ingredientsArray = []
    const ingredientsNameArray = []
    const recipeItem = await getData('recipes')
    const fetchImages = await getData('images')
    const getInstructions = await getData('instructions')
    const getIngredientsName = await getRecipeIngredients()
    ingredients(getIngredientsName)
    const getIngredients = await getData('recipe_ingredients')
    const displayItem = recipeItem.find((el) => el.recipeId == match.params.id);
    setItem(displayItem);

    getInstructions.forEach((element) => {
      if (element.recipeId == match.params.id) instructionsArray.push(element.instruction)
    });

    getIngredients.forEach((element) => {
      console.log(element);
      if (element.recipeId == match.params.id) ingredientsArray.push(element.ingredientId)
    });

    getIngredientsName.forEach(item => {
      if ((ingredientsArray.includes(item.ingredientId))) {
        console.log(item.ingredientId);
        ingredientsNameArray.push(item.name)
      }
    })

    setIngredientsName(getIngredientsName)
    setInstructions(instructionsArray)
    setIngredients(ingredientsNameArray)

    fetchImages.forEach((element) => {
      if (element.recipeId == match.params.id) imagesArray.push(element)

    });

    setImages(imagesArray);
    console.log('ind', ingredientsArray);
  }, []);

  return (
    <div className="form-inner product">
      <Carousel fade>
        {images.length > 0 &&
          images.map((image, i) => (
            <Carousel.Item key={i}>
              <img src={image.imgUrl} alt="1" height='20px' />

              <Carousel.Caption>
                <h3 className="text-center">{item.name}</h3>
                <p>{item.description}</p>
              </Carousel.Caption>
            </Carousel.Item>
          ))}
      </Carousel>
      <div>
        <ListGroupItem> name: {item.name}</ListGroupItem>
        <ListGroupItem> prep time: {item.prep_time_min} minutes</ListGroupItem>
        <ListGroupItem
          data-toggle="collapse"
          data-target="#collapseExample"
          aria-expanded="false"
          aria-controls="collapseExample"
        >
          ingredients-click to see
        </ListGroupItem>
        <ListGroupItem
          data-toggle="collapse"
          data-target="#collapseExample1"
          aria-expanded="false"
          aria-controls="collapseExample1"
        >
          {" "}
          instrictions-click to see
        </ListGroupItem>

        <div class="collapse" id="collapseExample">
          <div class="card card-body">
            {ingredients.length > 0 && ingredients.map((inst, i) => {

              return <li key={i}>{inst} </li>
            })}
          </div>

        </div>
        <div class="collapse" id="collapseExample1">

          {instructions.length > 0 && instructions.map((inst, i) => {

            return <li key={i}>{inst} </li>
          })}
        </div>
      </div>
      <div>
        <Link className="btn btn-primary m-2" to={`/edit/${item.recipeId}`}>
          Edit
        </Link>
        <Link className="btn btn-success">
          <div>
            <button onClick={() => addToCart(indredientsName)} >Add To Cart</button>
          </div>
        </Link>
      </div>
    </div>
  );
}

export default ItemDetails;
