import React, { useContext } from "react";
import Navbar from "./components/sections/NavBar";
import { useState } from "react";
// import "./App.css";
import Home from "./components/Pages/Home";
import { BrowserRouter as Router, Switch, Route, Redirect } from "react-router-dom";
import RegisterForm from "./components/forms/RegisterForm";
import NewRecipeForm from "./components/forms/NewRecipeForm";
import Login from "./components/forms/Login";
import Cart from "./components/Pages/Cart/Cart";
import ItemDetails from "./components/utills/ItemDetails";
import Footer from "./components/sections/Footer";
import EditRecipe from "./components/forms/EditRecipe";
import SearchRecipes from "./components/sections/SearchRecipes";
import Basket from "./components/Pages/Cart/Basket";
import Profile from "./components/Pages/Profile";
import Gallery from "./components/Pages/Gallery";
import LocalCart from "./components/Pages/Cart/LocalCart";
import AuthApi from "./components/context/AuthApi";

function App() {
  const Auth = useContext(AuthApi)
  const [auth, setAuth] = useState(false)

  const [cartItems, setCartItems] = useState([]);
  const onAdd = (product) => {
    const exist = cartItems.find((x) => x.id === product.id);
    if (exist) {
      setCartItems(
        cartItems.map((x) =>
          x.id === product.id ? { ...exist, qty: exist.qty + 1 } : x
        )
      );
    } else {
      setCartItems([...cartItems, { ...product, qty: 1 }]);
    }
  };
  const onRemove = (product) => {
    const exist = cartItems.find((x) => x.id === product.id);
    if (exist.qty === 1) {
      setCartItems(cartItems.filter((x) => x.id !== product.id));
    } else {
      setCartItems(
        cartItems.map((x) =>
          x.id === product.id ? { ...exist, qty: exist.qty - 1 } : x
        )
      );
    }
  };
  const Routes = () => {
    return <AuthApi.Provider value={{ auth, setAuth }}>
      <Router>
        <Navbar />
        <Switch>
          <Route path="/" exact component={Home} />

          {/* </Switch><Route path="/products" exact component={</SearchRecipes>} /> */}
          <Route exact path="/products" >
            <Gallery onAdd={onRemove} />
          </Route>
          <Route path="/sign-up" exact component={RegisterForm} />
          <Route path="/new-recipe" exact component={NewRecipeForm} />
          <Route path="/cart/:id" component={Cart} />
          <Route path="/basket" exact component={Basket} />
          <Route path="/products/:id" component={ItemDetails} />
          <Route path="/edit/:id" component={EditRecipe} />

          <Route path="/newcart" exact component={LocalCart} />
          <ProtectedLogin path='/login' auth={Auth.auth} component={Login} />
          <ProtectedProfile path='/profile' auth={Auth.auth} component={Profile} />
          <Route path="/basket"><Basket
            cartItems={cartItems}
            onAdd={onAdd}
            onRemove={onRemove}
          /></Route>
        </Switch>
        {/* <Footer /> */}
        {/* <Route path="/profile" component={Profile} /> */}
      </Router>

    </AuthApi.Provider >
  }
  const ProtectedLogin = ({ auth, component: Component, ...rest }) => {
    return (
      <Route
        {...rest}
        render={() => !auth ? (
          < Component />) : <Redirect to={'/profile'} />}

      />)
  }
  const ProtectedProfile = ({ auth, component: Component, ...rest }) => {
    return (
      <Route
        {...rest}
        render={() => auth ? (
          < Component />) : <Redirect to={'/login'} />}

      />)


  }





}

export default App;
