import React from "react";
import "./registerform.css";
// import { useHistory } from "react-router-dom";
import {
  Form,
  Col,
  InputGroup,
  ToggleButton,
  Button,
  ButtonGroup,
  Row,
} from "react-bootstrap";
import InputCreator from "./InputCreator";
import validate from "./validateInfo";
import useForm from "./useForm";
// import "./AddUser.css";
import ErrorMessages from "./ErrorMessages";

const RegisterForm = (props) => {
  //   const history = useHistory();
  const { handleSubmit, handleChange, values, errors } = useForm(
    submit,
    validate
  );

  function submit(e) {
    props.addUser(values);
    // history.push("/users");
  }

  return (
    <Form
      dir="rtl"
      className="text-left container w-50 m-auto rounded rtl"
      onSubmit={handleSubmit}
    >
      <Form.Row className="m-auto rtl">
        <h1 className="text-center">רישום למחברת מתכונים</h1>
      </Form.Row>
      <hr />
      <Form.Group controlId="formGridEmail" className="rtl">
        <InputCreator
          title="שם משתמש"
          type="text"
          required="true"
          value={values.username}
          placeholder="שם משתמש"
          sign={<i class="fas fa-user"></i>}
          onChange={handleChange}
          name="username"
          error={errors.username}
        />
        {errors.username && <ErrorMessages errors={errors.username} />}
      </Form.Group>
      <Form.Group controlId="formGridPassword">
        <InputCreator
          title="אימייל"
          type="email"
          required="true"
          value={values.email}
          placeholder="כתובת מייל הנמצאת בשימוש"
          sign={<i class="fas fa-mail-bulk"></i>}
          onChange={handleChange}
          name="email"
          error={errors.email}
        />
        {errors.email && <ErrorMessages errors={errors.email} />}
      </Form.Group>
      <Form.Group>
        <InputCreator
          title="סיסמא"
          type="password"
          required="true"
          value={values.password}
          placeholder="סיסמא"
          sign={<i class="fas fa-mail-bulk"></i>}
          onChange={handleChange}
          name="password"
          error={errors.password}
          note=" דרישות מינימום לסיסמא תקינה: לפחות ספרה, אות קטנה ואות גדולה
            (באנגלית). ולא פחות מ-8 תווים"
        />
        {errors.password && <ErrorMessages errors={errors.password} />}
      </Form.Group>
      <Form.Group>
        <InputCreator
          title="אימות סיסמא"
          type="password"
          required="true"
          value={values.password2}
          placeholder=""
          sign={<i class="fas fa-mail-bulk"></i>}
          onChange={handleChange}
          name="password2"
          error={errors.password2}
          note="תוכן השדה יהיה זהה לתוכן שהוקלד עבור הסיסמא"
        />
        {errors.password2 && <ErrorMessages errors={errors.password2} />}
      </Form.Group>
      <Form.Group>
        <Row>
          <Col>דברים שאני רוצה</Col>
          <Col className="w-100 ml-5">
            <row className="d-flex ">
              <Form.Check type="checkbox" label="טבעוני" value="vegeterian" />
              <Form.Check type="checkbox" label="פרווה" />
              <Form.Check type="checkbox" label="כשר" />
              <Form.Check type="checkbox" label="צמחוני" />
            </row>
            <row className="d-flex">
              <Form.Check
                type="checkbox"
                label="ללא גלוטן"
                value="vegeterian"
              />
              <Form.Check type="checkbox" label="פליאו" />
              <Form.Check type="checkbox" label="קטנוני" />
              <Form.Check type="checkbox" label="רואו" />
            </row>
          </Col>
        </Row>
      </Form.Group>
      <Row>
        <Button
          className="btn btn-primary my-2 text-right font-weight-bold "
          type="submit"
          onClick={handleSubmit}
        >
          רישום
        </Button>

        <p className="mr-3">יש למלא בצורה תקינה את השדות </p>
        <small h>יש לך כבר משתמש? לחץ כאן כדי להיכנס</small>
      </Row>
    </Form>
  );
};

export default RegisterForm;
