import { createContext, useEffect, useState } from "react";
import { getRecipes } from "../DAL/api";
import { v4 as uuidv4 } from "uuid";

export const recipeContext = createContext();

const RecipeContextProvider = (props) => {
  const [recipes, setRecipes] = useState([]);

  useEffect(async () => {
    const fetchedRecipes = await getRecipes();
    setRecipes(fetchedRecipes);
  }, []);

//   useEffect(() => {
//     localStorage.setItem("employees", JSON.stringify(employees));
//   });

  const sortedRecipes = recipes.sort((a, b) => (a.name < b.name ? -1 : 1));

  const addRecipe = (recipe) => {
    setRecipes([
      ...recipes,
      { ...recipe, recipeId: Math.floor(Math.random() * 121) },
    ]);
  };

  const deleteRecipe = (id) => {
    setRecipes(recipes.filter((recipe) => recipe.recipeId !== id));
  };

  const editRecipe = (id, updatedRecipe) => {
    setRecipes(
      recipes.map((recipe) => (recipe.recipeId === id ? updatedRecipe : recipe))
    );
  };

  return (
    <RecipeContext.Provider
      value={{ sortedRecipes, addRecipe, deleteRecipe, updateRecipe }}
    >
      {props.children}
    </RecipeContext.Provider>
  );
};

export default EmployeeContextProvider;
