import React from "react";
import "./registerform.css";
// import { useHistory } from "react-router-dom";
import {
  Form,
  Col,
  InputGroup,
  ToggleButton,
  Button,
  ButtonGroup,
  Row,
} from "react-bootstrap";
import InputCreator from "./InputCreator";
import validate from "./validateInfo";
import useForm from "./useForm";
// import "./AddUser.css";
import ErrorMessages from "./ErrorMessages";

const RegisterForm = (props) => {
  //   const history = useHistory();
  const { handleSubmit, handleChange, values, errors } = useForm(
    submit,
    validate
  );

  function submit(e) {
    props.addUser(values);
    // history.push("/users");
  }

  return (
    <Form
      dir="rtl"
      className="text-left container w-50 m-auto rounded rtl"
      onSubmit={handleSubmit}
    >
      <Form.Row className="m-auto rtl">
        <h1 className="text-center">רישום למחברת מתכונים</h1>
      </Form.Row>

      <hr />

      <Form.Group controlId="formGridEmail" className="rtl">
        <InputCreator
          title="שם משתמש"
          type="text"
          required="true"
          value={values.username}
          placeholder="שם משתמש"
          sign={<i class="fas fa-user"></i>}
          onChange={handleChange}
          name="username"
          error={errors.username}
        />
        {errors.username && <ErrorMessages errors={errors.username} />}
      </Form.Group>

      <Form.Group controlId="formGridPassword">
        <InputCreator
          title="אימייל"
          type="email"
          required="true"
          value={values.email}
          placeholder="כתובת מייל הנמצאת בשימוש"
          sign={<i class="fas fa-mail-bulk"></i>}
          onChange={handleChange}
          name="email"
          error={errors.email}
        />
        {errors.username && (
          <ul>
            {errors.email.map((error, i) => (
              <li key={i}>{error}</li>
            ))}
          </ul>
        )}
      </Form.Group>
      <Form.Group>
        <InputCreator
          title="סיסמא"
          type="password"
          required="true"
          value={values.email}
          placeholder="סיסמא"
          sign={<i class="fas fa-mail-bulk"></i>}
          onChange={handleChange}
          name="email"
          error={errors.email}
        />
        <small>דרישות מינימום לסיסמא תקינה: לפחות ספרה, אות קטנה ואות גדולה (באנגלית). ולא פחות מ-8 תווים </small>
      </Form.Group>
      <Form.Group>
        <InputCreator
          title="אימות סיסמא"
          type="password"
          required="true"
          value={values.email}
          placeholder=""
          sign={<i class="fas fa-mail-bulk"></i>}
          onChange={handleChange}
          name="email"
          error={errors.email}
          className="rtl"
        />
        <small>תוכן השדה יהיה זהה לתוכן שהוקלד עבור הסיסמה</small>
      </Form.Group>

      <Form.Group controlId="formBasicCheckbox">
        <Form.Check type="checkbox" label="טבעוני" value="vegeterian" />
        <Form.Check type="checkbox" label="פרווה" />
        <Form.Check type="checkbox" label="כשר" />
        <Form.Check type="checkbox" label="צמחוני" />
        <Form.Check type="checkbox" label="בשרי" />
        <Form.Check type="checkbox" label="Check me out" />
      </Form.Group>

      <Button
        className="btn btn-primary my-2 w-100 font-weight-bold "
        type="submit"
        onClick={handleSubmit}
      >
        Submit
      </Button>
    </Form>
  );
};

export default RegisterForm;
