import React, { useState, useContext, useEffect } from "react";

import { recipeContext } from "../context/RecipesContext";

import { GlobalContext } from "../context/GlobalState";
import { Link, useHistory } from "react-router-dom";
import { Form, FormGroup, Button } from "react-bootstrap";
import { getRecipes } from "../DAL/api";

function EditRecipe({ match }) {
  const { editUser, users } = useContext(GlobalContext);
  const [selectedRecipe, setSelectedRecipe] = useState({});
  console.log("editrecipe", match);
  const history = useHistory();
  const currenRecipeId = match.params.id;
  console.log(currenRecipeId);
  useEffect(async () => {
    let newrecipe = "";
    const recipes = await getRecipes();
    console.log(recipes);
    recipes.forEach((recipe) => {
      if (recipe.recipeId == currenRecipeId) newrecipe = recipe;
    });
    setSelectedRecipe(newrecipe);
  }, [currenRecipeId, users]);

  const onChange = (e) => {
    setSelectedRecipe({ ...selectedRecipe, [e.target.name]: e.target.value });
  };
  console.log("recipe", selectedRecipe);
  const onSubmit = (e) => {
    e.preventDefault();
    editUser(selectedRecipe);
    history.push("/");
  };

  return (
    <Form onSubmit={onSubmit}>
      <div className="form-inner">
        <FormGroup>
          <label>Name</label>
          <input
            type="text"
            value={selectedRecipe.name}
            onChange={onChange}
            name="name"
            placeholder="Enter user"
            required
          />
        </FormGroup>
        <Button type="submit">Edit Name</Button>
        <Link to="/" className="btn btn-danger ml-2">
          Cancel
        </Link>
      </div>
    </Form>
  );
}

export default EditRecipe;

const EditForm = ({ recipe }) => {
  const id = recipe.recipeId;

  const [values, setValues] = useState({...recipe});
  

  const { EditRecipe } = useContext(RecipeContext);

  const updatedEmployee = { ...recipe };

  const handleSubmit = (e) => {
    e.preventDefault();
    updateEmployee(id, updatedEmployee);
  };

  return (
    <Form onSubmit={handleSubmit}>
      <Form.Group>
        <Form.Control
          type="text"
          placeholder="Name *"
          name="name"
         
        
          required
        />
      </Form.Group>
      <Form.Group>
        <Form.Control
          type="email"
          placeholder="Email *"
          name="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
      </Form.Group>
      <Form.Group>
        <Form.Control
          as="textarea"
          placeholder="Address"
          rows={3}
          name="address"
        />
      </Form.Group>
      <Form.Group>
        <Form.Control
          type="text"
          placeholder="Phone"
          name="phone"
         
        />
      </Form.Group>
      <Button variant="success" type="submit" block>
        Edit Employee
      </Button>
    </Form>
  );
};

