import { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import Login from '../'
import ShowAllRecipes from "../sections/ShowAllRecipes";
import CardItem from "../sections/CardItem";
import Cookies from "js-cookie"
const { getData, getUserRecipes } = require('../DAL/api')


function Profile() {
    const history = useHistory()
    const [isLoged, setIsLoged] = useState(false)
    const [recipes, setRecipes] = useState([])
    const [images, setImages] = useState([])


    useEffect(async () => {
        const user = Cookies.get('user')
        let recipeId;
        const fetchRecipes = await getUserRecipes(user)
        setRecipes(fetchRecipes)
        if (fetchRecipes.length) recipeId = fetchRecipes[0].recipeId
        const fetchImages = await getData('images')
        setImages(fetchImages)

        if (isLoged) {
            history.push('/login')
        }
    }, [])
