import React, { useState, useContext, useEffect } from "react";

import { RecipesContext } from "../context/RecipesContext";

import { GlobalContext } from "../context/GlobalState";
import { Link, useHistory } from "react-router-dom";
import { Form, FormGroup, Button } from "react-bootstrap";
import { getRecipes } from "../DAL/api";
import RegisterForm from "./RegisterForm";

function EditRecipe({ match,recipe }) {
  const { editUser, users } = useContext(GlobalContext);
  const [selectedRecipe, setSelectedRecipe] = useState({});
  const [values, setValues] = useState({ ...recipe });
  const { editRecipe } = useContext(RecipesContext);

  console.log("editrecipe", match);
  const history = useHistory();
  const currenRecipeId = match.params.id;
  console.log(currenRecipeId);
  useEffect(async () => {
    let newrecipe = "";
    const recipes = await getRecipes();
    console.log(recipes);
    recipes.forEach((recipe) => {
      if (recipe.recipeId == currenRecipeId) newrecipe = recipe;
    });
    setSelectedRecipe(newrecipe);
  }, [currenRecipeId, users]);

  const onChange = (e) => {
    setSelectedRecipe({ ...selectedRecipe, [e.target.name]: e.target.value });
  };
  console.log("recipe", selectedRecipe);
  const onSubmit = (e) => {
    e.preventDefault();
    editUser(selectedRecipe);
    history.push("/");
  };
  const handleSubmit = (e) => {
    e.preventDefault();
  };
  return (
    <Form onSubmit={handleSubmit}>
<div className="form-inner">


    <RegisterForm recipe={recipe}/>
      <Form.Group>
        <Form.Control type="text" placeholder="Name *" name="name" required />
      </Form.Group>
      <Form.Group>
        <Form.Control
          type="email"
          placeholder="Email *"
          name="email"
          required
        />
      </Form.Group>
      <Form.Group>
        <Form.Control
          as="textarea"
          placeholder="Address"
          rows={3}
          name="address"
        />
      </Form.Group>
      <Form.Group>
        <Form.Control type="text" placeholder="Phone" name="phone" />
      </Form.Group>
      <Button variant="success" type="submit" block>
        Edit Employee
      </Button>
      </div>
    </Form>
  );
}

export default EditRecipe;

const EditForm = ({ recipe }) => {
  const id = recipe.recipeId;

  

  const updatedEmployee = { ...recipe };

  const handleSubmit = (e) => {
    e.preventDefault();
    EditRecipe(id, recipe);
  };

  return (
    <Form onSubmit={handleSubmit}>
      <Form.Group>
        <Form.Control type="text" placeholder="Name *" name="name" required />
      </Form.Group>
      <Form.Group>
        <Form.Control
          type="email"
          placeholder="Email *"
          name="email"
          required
        />
      </Form.Group>
      <Form.Group>
        <Form.Control
          as="textarea"
          placeholder="Address"
          rows={3}
          name="address"
        />
      </Form.Group>
      <Form.Group>
        <Form.Control type="text" placeholder="Phone" name="phone" />
      </Form.Group>
      <Button variant="success" type="submit" block>
        Edit Employee
      </Button>
    </Form>
  );
};

// export default EditForm;
