import React, { useState, useEffect } from "react";
import demoData from "./demoData";
import CardItem from "../../CardItem";
import { Form } from "react-bootstrap";
function SearchRecipes() {
  const dataList = demoData();
  const [searchText, setSearchText] = useState("");
  const [data, setData] = useState(dataList);
  const [order, setOrder] = useState("name");

  // exclude column list from filter
  const excludeColumns = ["id", "color"];

  // handle change event of search input
  const handleChange = (value) => {
    setSearchText(value);
    filterData(value);
  };

  // filter records by search text
  const filterData = (value) => {
    const lowercasedValue = value.toLowerCase().trim();
    if (lowercasedValue === "") setData(dataList);
    else {
      const filteredData = dataList.sort((a,b)=>{a[order]-b[order]}).filter((item) => {
        return Object.keys(item).some((key) =>
          excludeColumns.includes(key)
            ? false
            : item[key].toString().toLowerCase().includes(lowercasedValue)
        );
      });
      setData(filteredData);
    }
  };

  useEffect(() => {
    setData([data].sort((a, b) => a[order] - b[order]));
  }, [setData]);
  return (
    <div className="App">
      <br />
      <br />
      חיפוש:{" "}
      <input
        style={{ marginLeft: 5, "text-align": "center" }}
        type="text"
        placeholder="חפש מתכונים..."
        value={searchText}
        onChange={(e) => handleChange(e.target.value)}
      />
      <Form.Check
        type="radio"
        label="תאריך"
        value={"year"}
        name="public"
        onChange={() => {
          setData(data.sort((a, b) => a.name - b.name));
        }}
      />
      <Form.Check
        type="radio"
        value={"name"}
        label="שם"
        name="public"
        onChange={(e) => {
          console.log("name");
          setOrder(e.target.value);
        }}
      />
      <div className="box-container d-flex flex-wrap p-fixed text-center">
        {searchText.length > 0 &&
          data.map((d, i) => {
            return <CardItem text={d.name} label={d.year} src={d.src} />;
          })}
        <div className="clearboth"></div>
        {data.length === 0 && <span>לא נמצא</span>}
      </div>
    </div>
  );
}

export default SearchRecipes;
